import{_ as l,c as a,b as t,d,t as n,a as r,o as s}from"./app.f3774844.js";const u=JSON.parse('{"title":"\u6587\u6CD5\u7528\u8A9E\u306E\u89E3\u8AAC","description":"","frontmatter":{"title":"\u6587\u6CD5\u7528\u8A9E\u306E\u89E3\u8AAC","section":"\u8A00\u8A9E\u5B66\u306E\u6982\u8AAC"},"headers":[{"level":2,"title":"\u540D\u8A5E\u306B\u95A2\u3059\u308B\u7528\u8A9E","slug":"\u540D\u8A5E\u306B\u95A2\u3059\u308B\u7528\u8A9E"},{"level":3,"title":"\u6570","slug":"\u6570"},{"level":3,"title":"\u6027","slug":"\u6027"},{"level":3,"title":"\u683C","slug":"\u683C"},{"level":3,"title":"\u4EBA\u79F0","slug":"\u4EBA\u79F0"},{"level":2,"title":"\u52D5\u8A5E\u306B\u95A2\u3059\u308B\u7528\u8A9E","slug":"\u52D5\u8A5E\u306B\u95A2\u3059\u308B\u7528\u8A9E"},{"level":3,"title":"\u6642\u5236 (\u30C6\u30F3\u30B9) \u3068\u76F8 (\u30A2\u30B9\u30DA\u30AF\u30C8)","slug":"\u6642\u5236-\u30C6\u30F3\u30B9-\u3068\u76F8-\u30A2\u30B9\u30DA\u30AF\u30C8"},{"level":3,"title":"\u6CD5 (\u30E0\u30FC\u30C9)","slug":"\u6CD5-\u30E0\u30FC\u30C9"}],"relativePath":"docs/grammar/index.md","lastUpdated":1661332367000}'),i={name:"docs/grammar/index.md"},h={id:"frontmatter-title",tabindex:"-1"},g=t("a",{class:"header-anchor",href:"#frontmatter-title","aria-hidden":"true"},"#",-1),o=r('<h2 id="\u540D\u8A5E\u306B\u95A2\u3059\u308B\u7528\u8A9E" tabindex="-1">\u540D\u8A5E\u306B\u95A2\u3059\u308B\u7528\u8A9E <a class="header-anchor" href="#\u540D\u8A5E\u306B\u95A2\u3059\u308B\u7528\u8A9E" aria-hidden="true">#</a></h2><h3 id="\u6570" tabindex="-1">\u6570 <a class="header-anchor" href="#\u6570" aria-hidden="true">#</a></h3><p>\u6587\u6CD5\u306B\u304A\u3051\u308B\u6570\u3068\u306F, \u4F8B\u3048\u3070\u3082\u306E\u3092\u6570\u3048\u308B\u3068\u304D\u306B\u6570\u306B\u3088\u3063\u3066\u8A9E\u306E\u5F62\u304C\u5909\u5316\u3059\u308B\u8A00\u8A9E\u306B\u304A\u3044\u3066, \u305D\u306E\u5909\u5316\u306E\u3057\u304B\u305F\u3092\u6C7A\u3081\u308B\u6307\u6A19\u306E\u3053\u3068\u3092\u8A00\u3044\u307E\u3059. \u4F8B\u3048\u3070\u82F1\u8A9E\u3067, 1 \u3064\u306E\u3082\u306E\u306B\u5BFE\u3057\u3066\u5358\u6570\u5F62\u3092, \u305D\u308C\u4EE5\u5916\u306E\u3082\u306E\u306B\u306F\u8907\u6570\u5F62\u3092\u7528\u3044\u305F\u308A, 1 \u4EBA\u306E\u5C11\u5973\u304C\u6CF3\u3050\u3053\u3068\u3092\u8868\u73FE\u3059\u308B\u3068\u304D \u201CThe girl swims fast.\u201D \u306E\u3088\u3046\u306B\u52D5\u8A5E swim \u304C 3 \u4EBA\u79F0\u5358\u6570\u73FE\u5728\u5F62 swims \u306B\u5909\u5316\u3057\u305F\u308A\u3059\u308B\u3053\u3068\u306B\u76F8\u5F53\u3057\u307E\u3059. \u6570\u306F\u8A9E\u5F62\u3092\u6C7A\u3081\u308B\u30D1\u30E9\u30E1\u30FC\u30BF\u30FC\u306E\u3072\u3068\u3064\u306A\u306E\u3067\u3059. \u7279\u306B\u672C\u518A\u5B50\u3067\u7D39\u4ECB\u3059\u308B\u30E8\u30FC\u30ED\u30C3\u30D1\u306E 6 \u8A00\u8A9E\u3067\u306F, \u6570\u306E\u30D1\u30E9\u30E1\u30FC\u30BF\u30FC\u306B\u3088\u3063\u3066\u540D\u8A5E\u3084\u4EE3\u540D\u8A5E, \u5F62\u5BB9\u8A5E, \u51A0\u8A5E, \u6570\u8A5E, \u52D5\u8A5E\u306A\u3069\u304C\u5909\u5316\u3057\u307E\u3059 (\u6570\u8A5E\u304C\u6570\u306B\u3088\u3063\u3066\u5909\u5316\u3059\u308B\u306A\u3093\u3066\u5909\u3060\u3068\u601D\u308F\u308C\u305F\u65B9\u3078. \u4F8B\u3048\u3070\u30ED\u30B7\u30A2\u8A9E\u306B\u306F 1 \u3092\u610F\u5473\u3059\u308B odin \u306E\u8907\u6570\u5F62 odni \u3068\u3044\u3046\u3082\u306E\u304C\u3042\u308A\u307E\u3059. \u300C\u30AF\u30EA\u30B9\u30DE\u30B9\u306B<strong>\u4E00\u4EBA\u307C\u3063\u3061</strong>\u300D\u306F &quot;\u044F \u043E\u0434\u0438\u043D <em>ja odin</em>&quot;, \u300C\u30AF\u30EA\u30B9\u30DE\u30B9\uFF0C\u30AB\u30C3\u30D7\u30EB\u3067<strong>\u4E8C\u4EBA\u304D\u308A</strong>\u300D\u306F &quot;\u043C\u044B \u043E\u0434\u043D\u0438 <em>my odni</em>&quot; \u3067\u3059.) .</p><p>\u3053\u3053\u307E\u3067\u306E\u8AAC\u660E\u3067 \u300C\u306A\u3093\u3060, \u8981\u306F\u5358\u6570\u30FB\u8907\u6570\u306E\u533A\u5225\u306E\u3053\u3068\u3058\u3083\u306A\u3044\u304B\u300D \u3068\u601D\u308F\u308C\u305F\u65B9\u3082\u3044\u3089\u3063\u3057\u3083\u308B\u306E\u3067\u306F\u306A\u3044\u3067\u3057\u3087\u3046\u304B. \u5B9F\u306F, \u4E16\u306E\u4E2D\u306B\u306F\u5358\u6570\u30FB\u8907\u6570\u4EE5\u5916\u306B\u53CC\u6570\u3084\u4E09\u6570\u3068\u3044\u3046\u533A\u5225\u3092\u3082\u3064\u8A00\u8A9E\u3082\u3042\u308A\u307E\u3059. \u53CC\u6570\u306F\u5B57\u306E\u3054\u3068\u304F2\u3064\u306E\u3082\u306E\u3092, \u4E09\u6570\u306F3\u3064\u306E\u3082\u306E\u3092\u8868\u73FE\u3059\u308B\u3068\u304D\u306B\u4F7F\u3044\u307E\u3059. \u7279\u306B\u53CC\u6570\u306F\u30A2\u30E9\u30D3\u30A2\u8A9E\u3084\u30B9\u30ED\u30D9\u30CB\u30A2\u8A9E, \u53E4\u5178\u30AE\u30EA\u30B7\u30A2\u8A9E, \u30B5\u30F3\u30B9\u30AF\u30EA\u30C3\u30C8\u306A\u3069\u3067\u898B\u3089\u308C\u307E\u3059. \u307E\u305F\u30ED\u30B7\u30A2\u8A9E\u3084\u30DD\u30FC\u30E9\u30F3\u30C9\u8A9E\u306F, \u304B\u3064\u3066\u53CC\u6570\u306E\u533A\u5225\u3092\u3082\u3063\u3066\u3044\u305F\u540D\u6B8B\u3067, 1, 2~4, 5 \u4EE5\u4E0A\u3068\u3044\u3046\u5206\u985E\u306B\u3088\u308A\u5F8C\u308D\u306E\u540D\u8A5E\u3092\u5909\u5316\u3055\u305B\u307E\u3059 (<a href="/docs/rus/">\u30ED\u30B7\u30A2\u8A9E</a>\u3092\u53C2\u7167\u306E\u3053\u3068) .</p><h3 id="\u6027" tabindex="-1">\u6027 <a class="header-anchor" href="#\u6027" aria-hidden="true">#</a></h3><p>\u6587\u6CD5\u306B\u304A\u3051\u308B\u6027\u3068\u306F, \u540D\u8A5E\u306E\u5206\u985E\u306E\u3072\u3068\u3064\u3067, \u6570\u3068\u540C\u3058\u304F\u95A2\u4FC2\u3059\u308B\u8A9E\u306E\u5F62\u3092\u6C7A\u3081\u308B\u30D1\u30E9\u30E1\u30FC\u30BF\u30FC\u3068\u306A\u308A\u307E\u3059. \u4F8B\u3048\u3070\u30C9\u30A4\u30C4\u8A9E\u306E\u4E0D\u5B9A\u51A0\u8A5E (\u82F1\u8A9E\u306E a, an) \u306F, \u7537\u6027\u540D\u8A5E\u306B\u5BFE\u3057\u3066 ein, \u5973\u6027\u540D\u8A5E\u306B\u5BFE\u3057\u3066 eine \u3068\u3044\u3046\u98A8\u306B, \u6027\u306B\u3088\u3063\u3066\u8A9E\u5F62\u304C\u5909\u5316\u3057\u307E\u3059.</p><table><thead><tr><th style="text-align:left;">\u30C9\u30A4\u30C4\u8A9E</th><th style="text-align:left;">\u65E5\u672C\u8A9E</th></tr></thead><tbody><tr><td style="text-align:left;">ein Junge</td><td style="text-align:left;">1 \u4EBA\u306E\u5C11\u5E74</td></tr><tr><td style="text-align:left;">eine Frau</td><td style="text-align:left;">1 \u4EBA\u306E\u5973\u6027</td></tr></tbody></table><p>\u30C1\u30A7\u30B3\u8A9E\u3067\u306F\u4E3B\u8A9E\u306E\u6027\u306B\u3088\u3063\u3066\u52D5\u8A5E\u306E\u904E\u53BB\u5F62\u306E\u8A9E\u5C3E (-l, -la) \u304C\u5909\u5316\u3057\u307E\u3059.</p><table><thead><tr><th style="text-align:left;">\u30C1\u30A7\u30B3\u8A9E</th><th style="text-align:left;">\u65E5\u672C\u8A9E</th></tr></thead><tbody><tr><td style="text-align:left;">\u010Cet<strong>l</strong> jsem knihu.</td><td style="text-align:left;">\u79C1 (\u7537) \u306F\u672C\u3092\u8AAD\u3093\u3060</td></tr><tr><td style="text-align:left;">\u010Cet<strong>la</strong> jsem knihu.</td><td style="text-align:left;">\u79C1 (\u5973) \u306F\u672C\u3092\u8AAD\u3093\u3060</td></tr></tbody></table><p>\u65E5\u672C\u8A9E\u306B\u306F\u3053\u306E\u3088\u3046\u306A\u533A\u5225\u304C\u306A\u3044\u305F\u3081\u611F\u899A\u304C\u5206\u304B\u308A\u306B\u304F\u3044\u3067\u3059\u304C, \u4F8B\u3048\u3070\u308A\u3093\u3054\u306B\u5BFE\u3057\u3066\u300C\u500B\u300D, \u925B\u7B46\u306B\u5BFE\u3057\u3066\u300C\u672C\u300D, \u72AC\u306B\u5BFE\u3057\u3066\u300C\u5339\u300D\u3068\u3044\u3046\u3088\u3046\u306B\u540D\u8A5E\u3054\u3068\u306B\u52A9\u6570\u8A5E (\u6570\u3048\u65B9) \u3092\u4F7F\u3044\u5206\u3051\u308B\u3053\u3068\u3068\u5C11\u3057\u4F3C\u3066\u3044\u307E\u3059.</p><p>\u3068\u3053\u308D\u3067, \u300C\u6027\u300D\u3068\u3044\u3046\u547C\u3073\u65B9\u306F\u4E00\u4F53\u4F55\u306A\u306E\u3067\u3057\u3087\u3046\u304B. \u3057\u3070\u3057\u3070, \u6027\u3068\u3044\u3046\u8A00\u8449\u306B\u60D1\u308F\u3055\u308C\u3066 \u300C\u306A\u3093\u3067\u3082\u306E\u306B\u6027\u5225\u304C\u3042\u308B\u3093\u3060\u300D\u3068\u304B \u300C\u306A\u305C\u30D5\u30E9\u30F3\u30B9\u8A9E\u306E pomme (\u308A\u3093\u3054) \u306F\u5973\u6027\u306A\u306E\u3060\u308D\u3046\u300D, \u300C\u30ED\u30B7\u30A2\u8A9E\u3067 gora (\u5C71) \u304C\u5973\u6027\u540D\u8A5E\u3067\u3042\u308B\u306E\u306B\u306F\u4F55\u304B\u81EA\u7136\u4FE1\u4EF0\u7684\u306A\u610F\u5473\u304C\u3042\u308B\u3093\u3058\u3083\u306A\u304B\u308D\u3046\u304B\u300D\u306A\u3069\u3068\u52D8\u7E70\u308B\u4EBA\u304C\u3044\u307E\u3059. \u5B9F\u969B\u306E\u3068\u3053\u308D, \u3053\u308C\u3089\u306E\u7591\u554F\u306F\u3042\u307E\u308A\u610F\u5473\u3092\u306A\u3057\u307E\u305B\u3093. \u540D\u8A5E\u306E\u6027\u306F\u8A00\u8A9E\u306B\u3088\u3063\u3066\u306F\u751F\u7269\u5B66\u7684\u306A\u6027\u3068\u4E00\u81F4\u3057\u306A\u3044\u3053\u3068\u3082\u3042\u308A\u307E\u3059\u3057, \u975E\u751F\u7269\u540D\u8A5E\u306E\u6027\u306F\u5927\u62B5\u306E\u8A00\u8A9E\u3067\u306F\u30E9\u30F3\u30C0\u30E0\u306B\u6C7A\u307E\u3063\u3066\u3044\u3066\u7279\u5225\u306A\u610F\u5473\u306F\u3042\u308A\u307E\u305B\u3093. \u6027\u306E\u6982\u5FF5\u306F, \u6587\u6CD5\u306E\u4E0A\u3067\u306F\u5358\u306A\u308B\u540D\u8A5E\u306E\u5206\u985E\u3067\u3059. \u3053\u306E\u70B9\u3092\u305C\u3072\u982D\u306E\u4E2D\u306B\u5165\u308C\u3066\u304A\u304F\u3068\u826F\u3044\u3067\u3057\u3087\u3046.</p><h3 id="\u683C" tabindex="-1">\u683C <a class="header-anchor" href="#\u683C" aria-hidden="true">#</a></h3><p>\u683C\u3068\u306F, \u540D\u8A5E\u3084\u4EE3\u540D\u8A5E\u304C\u6587\u306E\u4E2D\u3067\u3069\u306E\u3088\u3046\u306A\u610F\u5473, \u4ED6\u306E\u8A9E\u3068\u306E\u95A2\u4FC2\u3092\u3082\u3063\u3066\u3044\u308B\u306E\u304B\u3092\u793A\u3059\u76EE\u5370\u306E\u3053\u3068\u3067\u3059. \u5177\u4F53\u7684\u306B\u306F \u4E3B\u8A9E (~\u304C), \u76EE\u7684\u8A9E (~\u3092, \u306B), \u6240\u6709 (~\u306E), \u5834\u6240 (~\u3067), \u624B\u6BB5 (~\u306B\u3088\u3063\u3066), \u547C\u3073\u304B\u3051 (~\u3088) \u306A\u3069\u3092\u8868\u3059\u76EE\u5370\u3067, \u82F1\u8A9E\u306E I-my-me \u3082\u305D\u306E\u4E00\u4F8B\u3067\u3059. \u683C\u3082\u307E\u305F, \u6027\u3084\u6570\u3068\u540C\u3058\u3088\u3046\u306B\u95A2\u4FC2\u3059\u308B\u8A9E\u306E\u5F62\u3092\u6C7A\u3081\u308B\u30D1\u30E9\u30E1\u30FC\u30BF\u30FC\u3068\u306A\u308B\u3053\u3068\u304C\u3042\u308A\u307E\u3059. \u4F8B\u3048\u3070\u30C9\u30A4\u30C4\u8A9E\u3067\u306F,</p><table><thead><tr><th style="text-align:left;">\u30C9\u30A4\u30C4\u8A9E</th><th style="text-align:left;">\u65E5\u672C\u8A9E</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>das</strong> niedlich<strong>e</strong> M\xE4dchen</td><td style="text-align:left;">\u305D\u306E\u611B\u3089\u3057\u3044\u5C11\u5973\u304C</td></tr><tr><td style="text-align:left;"><strong>des</strong> niedlich<strong>en</strong> M\xE4dchens</td><td style="text-align:left;">\u305D\u306E\u611B\u3089\u3057\u3044\u5C11\u5973\u306E</td></tr></tbody></table><p>\u3068\u3044\u3046\u98A8\u306B\u683C\u306B\u3088\u3063\u3066\u540D\u8A5E M\xE4dchen \u306E\u4ED6\u306B\u5B9A\u51A0\u8A5E das \u3068\u5F62\u5BB9\u8A5E niedlich \u3082\u5909\u5316\u3057\u307E\u3059. \u683C\u306E\u76EE\u5370\u306F\u540D\u8A5E\u306E\u8A9E\u5F62\u5909\u5316\u3067\u793A\u3055\u308C\u308B\u3053\u3068\u3082\u3042\u308C\u3070 \u524D\u7F6E\u8A5E\u3084\u5F8C\u7F6E\u8A5E, \u8A9E\u9806\u306B\u3088\u3063\u3066\u793A\u3055\u308C\u308B\u3053\u3068\u3082\u3042\u308A\u307E\u3059. \u8A9E\u5F62\u5909\u5316\u306B\u3088\u308B\u683C\u306E\u6A19\u793A\u306F\u53E4\u5178\u30AE\u30EA\u30B7\u30A2\u8A9E\u3084\u30E9\u30C6\u30F3\u8A9E, \u30ED\u30B7\u30A2\u8A9E, \u30C9\u30A4\u30C4\u8A9E\u306A\u3069\u3067\u306A\u3055\u308C, \u305D\u306E\u5909\u5316\u306E\u3053\u3068\u3092\u683C\u5909\u5316\u3068\u547C\u3073\u307E\u3059. \u82F1\u8A9E\u3084\u30D5\u30E9\u30F3\u30B9\u8A9E, \u30B9\u30DA\u30A4\u30F3\u8A9E, \u30A4\u30BF\u30EA\u30A2\u8A9E\u306F \u304B\u3064\u3066\u683C\u5909\u5316\u304C\u3042\u3063\u305F\u306E\u3067\u3059\u304C\u307B\u3068\u3093\u3069\u6D88\u5931\u3057\u3066\u3057\u307E\u3044, \u4EE3\u308F\u308A\u306B\u524D\u7F6E\u8A5E\u3068\u8A9E\u9806\u3067\u683C\u3092\u793A\u3057\u307E\u3059. \u5F8C\u7F6E\u8A5E\u306B\u3088\u308B\u683C\u6A19\u793A\u306F\u65E5\u672C\u8A9E\u3084\u671D\u9BAE\u8A9E\u306A\u3069\u304C\u5F53\u3066\u306F\u307E\u308A, \u5F8C\u7F6E\u8A5E\u306E\u3053\u3068\u3092\u65E5\u672C\u3067\u306F\u4E00\u822C\u306B\u683C\u52A9\u8A5E\u3042\u308B\u3044\u306F\u30C6\u30CB\u30F2\u30CF\u3068\u547C\u3073\u307E\u3059. \u4E2D\u56FD\u8A9E\u306F\u8A9E\u9806\u3068\u4ECB\u8A5E\u3068\u3044\u3046\u524D\u7F6E\u8A5E\u3067\u683C\u3092\u793A\u3059\u8A00\u8A9E\u3067\u3059.</p><table><thead><tr><th style="text-align:left;">\u4E2D\u56FD\u8A9E</th><th style="text-align:left;">\u65E5\u672C\u8A9E</th></tr></thead><tbody><tr><td style="text-align:left;">\u6211 \u5403 \u7092\u996D\u3002</td><td style="text-align:left;">\u79C1\u304C\u30C1\u30E3\u30FC\u30CF\u30F3\u3092\u98DF\u3079\u308B</td></tr><tr><td style="text-align:left;">\u7092\u996D \u5403 \u6211\u3002</td><td style="text-align:left;">\u30C1\u30E3\u30FC\u30CF\u30F3\u304C\u79C1\u3092\u98DF\u3079\u308B</td></tr></tbody></table><h3 id="\u4EBA\u79F0" tabindex="-1">\u4EBA\u79F0 <a class="header-anchor" href="#\u4EBA\u79F0" aria-hidden="true">#</a></h3><p>\u82F1\u8A9E\u5B66\u7FD2\u3067\u3082\u3088\u304F\u4F7F\u308F\u308C\u308B\u8A00\u8449\u306A\u306E\u3067\u77E5\u3063\u3066\u3044\u308B\u4EBA\u3082\u591A\u3044\u3067\u3057\u3087\u3046. 1 \u4EBA\u79F0\u306F\u8A71\u3057\u624B, 2 \u4EBA\u79F0\u306F\u805E\u304D\u624B, 3 \u4EBA\u79F0\u306F\u305D\u308C\u4EE5\u5916\u306E\u4EBA\u30FB\u3082\u306E\u3092\u6307\u3057\u307E\u3059. \u7279\u306B\u30A4\u30F3\u30C9\u30FB\u30E8\u30FC\u30ED\u30C3\u30D1\u8A9E\u3067\u306F\u4EBA\u79F0\u306B\u3088\u3063\u3066\u52D5\u8A5E\u304C\u6D3B\u7528\u3059\u308B\u3053\u3068\u304C\u591A\u3044\u3067\u3059. \u82F1\u8A9E\u3067\u306F\u4E3B\u8A9E\u304C 3 \u4EBA\u79F0\u5358\u6570\u306E\u3068\u304D\u306E\u307F\u52D5\u8A5E\u306E\u8A9E\u5C3E -s \u304C\u4ED8\u304D\u307E\u3059\u304C, \u4F8B\u3048\u3070\u30E9\u30C6\u30F3\u8A9E\u3067\u306F\u4E3B\u8A9E\u306E\u4EBA\u79F0\u3068\u6570\u306B\u3088\u3063\u3066\u305D\u308C\u305E\u308C\u6D3B\u7528\u5F62\u304C\u3042\u308A\u307E\u3059.</p><table><thead><tr><th style="text-align:center;"></th><th style="text-align:center;">\u5358\u6570</th><th style="text-align:center;">\u8907\u6570</th></tr></thead><tbody><tr><td style="text-align:center;">1 \u4EBA\u79F0</td><td style="text-align:center;">am<strong>o</strong> \u79C1\u306F\u611B\u3059\u308B</td><td style="text-align:center;">am<strong>amus</strong> \u79C1\u305F\u3061\u306F\u611B\u3059\u308B</td></tr><tr><td style="text-align:center;">2 \u4EBA\u79F0</td><td style="text-align:center;">am<strong>as</strong> \u541B\u306F\u611B\u3059\u308B</td><td style="text-align:center;">am<strong>atis</strong> \u541B\u305F\u3061\u306F\u611B\u3059\u308B</td></tr><tr><td style="text-align:center;">3 \u4EBA\u79F0</td><td style="text-align:center;">am<strong>at</strong> \u5F7C\u306F\u611B\u3059\u308B</td><td style="text-align:center;">am<strong>ant</strong> \u5F7C\u3089\u306F\u611B\u3059\u308B</td></tr></tbody></table><h2 id="\u52D5\u8A5E\u306B\u95A2\u3059\u308B\u7528\u8A9E" tabindex="-1">\u52D5\u8A5E\u306B\u95A2\u3059\u308B\u7528\u8A9E <a class="header-anchor" href="#\u52D5\u8A5E\u306B\u95A2\u3059\u308B\u7528\u8A9E" aria-hidden="true">#</a></h2><h3 id="\u6642\u5236-\u30C6\u30F3\u30B9-\u3068\u76F8-\u30A2\u30B9\u30DA\u30AF\u30C8" tabindex="-1">\u6642\u5236 (\u30C6\u30F3\u30B9) \u3068\u76F8 (\u30A2\u30B9\u30DA\u30AF\u30C8) <a class="header-anchor" href="#\u6642\u5236-\u30C6\u30F3\u30B9-\u3068\u76F8-\u30A2\u30B9\u30DA\u30AF\u30C8" aria-hidden="true">#</a></h3><p>\u3053\u306E\u3075\u305F\u3064\u306F\u4E00\u898B\u3059\u308B\u3068\u4F3C\u305F\u6982\u5FF5\u3067\u3059. \u6642\u5236\u306F\u51FA\u6765\u4E8B\u304C\u3044\u3064\u8D77\u304D\u305F\u306E\u304B\u3092\u793A\u3059\u306E\u306B\u5BFE\u3057, \u76F8\u306F\u51FA\u6765\u4E8B\u304C\u3069\u306E\u3088\u3046\u306A\u6642\u9593\u7684\u5C40\u9762\u3067\u8D77\u3053\u3063\u305F\u306E\u304B\u3092\u793A\u3057\u307E\u3059. \u79C1\u305F\u3061\u73FE\u4EE3\u4EBA\u306E\u30CB\u30E5\u30FC\u30C8\u30F3\u529B\u5B66\u7684\u6642\u9593\u89B3\u5FF5\u3067\u8868\u73FE\u3059\u308B\u306A\u3089\u3070, \u6570\u76F4\u7DDA\u306E\u539F\u70B9 (\u73FE\u5728) \u304B\u3089\u306E\u4F4D\u7F6E\u306B\u76F8\u5F53\u3059\u308B\u306E\u304C\u6642\u5236\u3067, \u6570\u76F4\u7DDA\u4E0A\u306E\u70B9\u3084\u7DDA\u5206\u306E\u5206\u5E03\u306B\u5F53\u305F\u308B\u306E\u304C\u76F8\u3067\u3059. \u70B9\u3084\u7DDA\u5206\u306E\u5206\u5E03\u306E\u3057\u304B\u305F\u304C\u540C\u3058\u3067\u3082, \u305D\u308C\u304C\u539F\u70B9\u304B\u3089\u5DE6\u5074\u306B\u3042\u308C\u3070\u904E\u53BB\u6642\u5236\u3067\u3059\u3057, \u539F\u70B9\u3068\u305D\u306E\u5468\u8FBA\u306B\u3042\u308C\u3070\u73FE\u5728\u6642\u5236\u306B\u306A\u308A\u307E\u3059. \u82F1\u8A9E\u306E\u73FE\u5728\u5B8C\u4E86\u5F62 have + (\u904E\u53BB\u5206\u8A5E) \u306F\u300C\u73FE\u5728\u6642\u5236\u306E\u5B8C\u4E86\u76F8\u300D\u3068\u3044\u3046\u610F\u5473\u3067\u3059.</p><p>\u300C\u76F8\u306F\u70B9\u3084\u7DDA\u5206\u306E\u5206\u5E03\u306E\u3057\u304B\u305F\u3067\u3042\u308B\u300D\u3068\u3044\u3046\u8AAC\u660E\u3067\u306F\u308F\u304B\u308A\u306B\u304F\u3044\u306E\u3067\u4F8B\u3092\u898B\u3066\u307F\u307E\u3057 \u3087\u3046.</p><table><thead><tr><th style="text-align:center;"></th><th style="text-align:left;">\u82F1\u8A9E</th><th style="text-align:left;">\u65E5\u672C\u8A9E</th></tr></thead><tbody><tr><td style="text-align:center;">a</td><td style="text-align:left;">I have lived in Beijing for 3 years.</td><td style="text-align:left;">\u79C1\u306F\u5317\u4EAC\u306B\u4E09\u5E74\u9593\u4F4F\u3093\u3067\u3044\u308B</td></tr><tr><td style="text-align:center;">b</td><td style="text-align:left;">I have seen the movie three times.</td><td style="text-align:left;">\u79C1\u306F\u305D\u306E\u6620\u753B\u3092\u4E09\u56DE\u898B\u305F</td></tr><tr><td style="text-align:center;">c</td><td style="text-align:left;">I have finished my homework.</td><td style="text-align:left;">\u79C1\u306F\u5BBF\u984C\u3092\u3084\u308A\u7D42\u3048\u305F</td></tr></tbody></table><p>a \u306E\u5834\u5408\u306F\u7D99\u7D9A\u3092\u8868\u3057\u3066\u3044\u3066, b \u306E\u5834\u5408\u306F\u7D4C\u9A13\u3092, c \u306E\u5834\u5408\u306F\u5B8C\u4E86\u3092\u793A\u3057\u3066\u3044\u307E\u3059. \u305D\u308C\u305E\u308C\u52D5\u4F5C\u306E\u6642\u9593\u8EF8\u4E0A\u306E\u5206\u5E03\u3092\u8868\u73FE\u3057\u3066\u3044\u308B\u306E\u304C\u308F\u304B\u308A\u307E\u3059. \u4E2D\u56FD\u8A9E\u3067\u306F\u3053\u308C\u3089\u306E\u9055\u3044\u3092\u660E\u793A\u3057\u307E\u3059.</p><table><thead><tr><th style="text-align:center;"></th><th style="text-align:left;">\u4E2D\u56FD\u8A9E</th><th style="text-align:left;">\u65E5\u672C\u8A9E</th></tr></thead><tbody><tr><td style="text-align:center;">a&#39;</td><td style="text-align:left;">\u6211 \u5728\u5317\u4EAC \u4F4F\u4E86 \u4E09\u5E74\u4E86\u3002</td><td style="text-align:left;">\u79C1\u306F\u5317\u4EAC\u306B\u4E09\u5E74\u9593\u4F4F\u3093\u3067\u3044\u308B</td></tr><tr><td style="text-align:center;">b&#39;</td><td style="text-align:left;">\u6211 \u770B\u8FC7 \u4E09\u6B21 \u90A3\u4E2A \u7535\u5F71\u3002</td><td style="text-align:left;">\u79C1\u306F\u305D\u306E\u6620\u753B\u3092\u4E09\u56DE\u898B\u305F</td></tr><tr><td style="text-align:center;">c&#39;</td><td style="text-align:left;">\u6211 \u505A\u5B8C \u4F5C\u4E1A\u3002</td><td style="text-align:left;">\u79C1\u306F\u5BBF\u984C\u3092\u3084\u308A\u7D42\u3048\u305F</td></tr></tbody></table><p>\u3068\u3053\u308D\u3067\u5B9F\u969B\u306E\u751F\u304D\u305F\u8A00\u8A9E\u3067\u306F, \u6642\u5236\u3068\u76F8\u306F\u4E0A\u3067\u8FF0\u3079\u305F\u307B\u3069\u7C21\u5358\u306B\u306F\u5206\u96E2\u3067\u304D\u306A\u3044\u5834\u5408\u304C\u3042\u308A\u307E\u3059. \u5B8C\u4E86\u76F8\u3068\u904E\u53BB\u6642\u5236\u304C\u307B\u3068\u3093\u3069\u540C\u4E00\u8996\u3055\u308C\u308B\u3053\u3068\u3082\u3042\u308A\u307E\u3059. \u4E2D\u56FD\u8A9E\u306E\u3088\u3046\u306B\u6642\u5236\u3092\u3082\u305F\u306A\u3044\u3067\u76F8\u3067\u8868\u73FE\u3059\u308B\u8A00\u8A9E\u3082\u3042\u308A\u307E\u3059. \u3057\u304B\u3057\u30B9\u30DA\u30A4\u30F3\u8A9E\u3084\u30ED\u30B7\u30A2\u8A9E, \u53E4\u5178\u30AE\u30EA\u30B7\u30A2\u8A9E\u306A\u3069\u306E\u3044\u304F\u3064\u304B\u306E\u8A00\u8A9E\u3067\u306F, \u6642\u5236\u3068\u76F8\u306E\u533A\u5225\u306F\u304B\u306A\u308A\u91CD\u8981\u306A\u554F\u984C\u306B\u306A\u3063\u3066\u304F\u308B\u306E\u3067\u899A\u3048\u3066\u304A\u304F\u3068\u826F\u3044\u3067\u3057\u3087\u3046.</p><h3 id="\u6CD5-\u30E0\u30FC\u30C9" tabindex="-1">\u6CD5 (\u30E0\u30FC\u30C9) <a class="header-anchor" href="#\u6CD5-\u30E0\u30FC\u30C9" aria-hidden="true">#</a></h3><p>\u6CD5\u3068\u306F, \u8A71\u984C\u306E\u73FE\u5B9F\u3068\u306E\u95A2\u9023\u3092\u8868\u73FE\u3059\u308B\u65B9\u6CD5\u3092, \u52D5\u8A5E\u306E\u5909\u5316\u306B\u57FA\u3065\u3044\u3066\u5206\u985E\u3057\u305F\u3082\u306E\u3067\u3059. \u8A71\u984C\u306E\u73FE\u5B9F\u3068\u306E\u95A2\u9023\u3092\u8868\u73FE\u3059\u308B\u65B9\u6CD5\u5168\u822C\u306E\u3053\u3068\u3092\u30E2\u30C0\u30EA\u30C6\u30A3 (\u6CD5\u6027) \u3068\u547C\u3073\u307E\u3059\u304C, \u305D\u306E\u3046\u3061\u52D5\u8A5E\u306E\u5909\u5316\u3092\u5F15\u304D\u8D77\u3053\u3059\u3082\u306E\u304C\u6CD5\u3068\u3044\u3046\u6982\u5FF5\u3067\u3059. \u30E2\u30C0\u30EA\u30C6\u30A3\u304C\u3042\u3063\u3066\u3082, \u305D\u308C\u304C\u52D5\u8A5E\u306E\u5909\u5316\u3092\u5F15\u304D\u8D77\u3053\u3055\u306A\u3051\u308C\u3070, \u305D\u306E\u8A00\u8A9E\u306B\u6CD5\u306F\u306A\u3044\u3068\u3044\u3046\u3053\u3068\u306B\u306A\u308A\u307E\u3059. \u3053\u306E\u7528\u8A9E\u306F\u63B4\u307F\u3069\u3053\u308D\u304C\u306A\u304F, \u7406\u89E3\u3057\u3065\u3089\u3044\u3068\u3053\u308D\u304C\u3042\u308B\u3068\u601D\u308F\u308C\u307E\u3059\u306E\u3067, \u4EE5\u4E0B\u306E\u4F8B\u3092\u53C2\u8003\u306B\u3057\u3066\u307F\u3066\u304F\u3060\u3055\u3044. \u8A00\u8A9E\u306B\u3088\u3063\u3066\u306F 2 \u3064\u306E\u6CD5\u306E\u6A5F\u80FD\u304C\u307E\u3068\u307E\u3063\u30661\u3064\u306E\u6CD5\u306B\u306A\u308B\u5834\u5408\u3082\u3042\u308A\u307E\u3059.</p><table><thead><tr><th style="text-align:center;">\u7A2E\u985E</th><th style="text-align:left;">\u6A5F\u80FD</th></tr></thead><tbody><tr><td style="text-align:center;">\u76F4\u8AAC\u6CD5</td><td style="text-align:left;">\u8A71\u984C\u304C\u73FE\u5B9F\u306B\u57FA\u3065\u3044\u3066\u3044\u308B\u3068\u304D\u306B\u7528\u3044\u3089\u308C\u308B, \u57FA\u672C\u7684\u306A\u6CD5</td></tr><tr><td style="text-align:center;">\u6761\u4EF6\u6CD5</td><td style="text-align:left;">\u8A71\u984C\u306F\u6761\u4EF6\u4ED8\u304D\u3067\u3042\u308A\u3048\u308B, \u3068\u3044\u3046\u3053\u3068\u3092\u8868\u73FE\u3059\u308B</td></tr><tr><td style="text-align:center;">\u63A5\u7D9A\u6CD5</td><td style="text-align:left;">\u8A71\u984C\u304C\u975E\u73FE\u5B9F\u7684\u306A\u3068\u304D, \u5B9F\u969B\u306B\u8D77\u3053\u308B\u304B\u308F\u304B\u3089\u306A\u3044\u3068\u304D, <br> \u9858\u671B\u3068\u3057\u3066\u4ED6\u8005\u306B\u4E01\u5BE7\u306B\u304A\u9858\u3044\u3059\u308B\u3068\u304D\u306A\u3069\u306B\u7528\u3044\u3089\u308C\u308B</td></tr><tr><td style="text-align:center;">\u547D\u4EE4\u6CD5</td><td style="text-align:left;">\u8A71\u984C\u304C\u547D\u4EE4\u3067\u3042\u308B\u3068\u304D\u306B\u4F7F\u3046</td></tr><tr><td style="text-align:center;">\u5E0C\u6C42\u6CD5</td><td style="text-align:left;">\u610F\u5FD7\u3084\u9858\u671B\u3092\u8868\u73FE\u3059\u308B</td></tr></tbody></table>',30);function y(e,x,c,f,b,p){return s(),a("div",null,[t("h1",h,[d(n(e.$frontmatter.title)+" ",1),g]),o])}const _=l(i,[["render",y]]);export{u as __pageData,_ as default};
