import{_ as l,c as n,b as t,d,t as r,a,o as i}from"./app.f3774844.js";const _=JSON.parse('{"title":"\u53E4\u5178\u30AE\u30EA\u30B7\u30A2\u8A9E / \u1F19\u03BB\u03BB\u03B7\u03BD\u03B9\u03BA\u03AE","description":"","frontmatter":{"title":"\u53E4\u5178\u30AE\u30EA\u30B7\u30A2\u8A9E / \u1F19\u03BB\u03BB\u03B7\u03BD\u03B9\u03BA\u03AE","section":"\u8A00\u8A9E\u3054\u3068\u306E\u6982\u8AAC"},"headers":[{"level":2,"title":"\u6027, \u6570, \u683C","slug":"\u6027-\u6570-\u683C"},{"level":3,"title":"\u6027","slug":"\u6027"},{"level":3,"title":"\u6570","slug":"\u6570"},{"level":3,"title":"\u683C","slug":"\u683C"},{"level":2,"title":"\u52D5\u8A5E\u7D44\u7E54","slug":"\u52D5\u8A5E\u7D44\u7E54"},{"level":2,"title":"\u4EBA\u79F0","slug":"\u4EBA\u79F0"},{"level":2,"title":"\u6570","slug":"\u6570-1"},{"level":2,"title":"\u6CD5","slug":"\u6CD5"},{"level":3,"title":"\u76F4\u8AAC\u6CD5","slug":"\u76F4\u8AAC\u6CD5"},{"level":3,"title":"\u63A5\u7D9A\u6CD5","slug":"\u63A5\u7D9A\u6CD5"},{"level":3,"title":"\u5E0C\u6C42\u6CD5","slug":"\u5E0C\u6C42\u6CD5"},{"level":3,"title":"\u547D\u4EE4\u6CD5","slug":"\u547D\u4EE4\u6CD5"},{"level":2,"title":"\u30A2\u30B9\u30DA\u30AF\u30C8, \u6642\u5236","slug":"\u30A2\u30B9\u30DA\u30AF\u30C8-\u6642\u5236"},{"level":2,"title":"\u614B","slug":"\u614B"},{"level":2,"title":"\u8A9E\u9806","slug":"\u8A9E\u9806"},{"level":2,"title":"\u6587\u5B57","slug":"\u6587\u5B57"},{"level":2,"title":"\u53C2\u8003\u6587\u732E","slug":"\u53C2\u8003\u6587\u732E"}],"relativePath":"docs/grc/index.md","lastUpdated":1661322361000}'),s={name:"docs/grc/index.md"},g={id:"frontmatter-title",tabindex:"-1"},c=t("a",{class:"header-anchor",href:"#frontmatter-title","aria-hidden":"true"},"#",-1),x=a('<h2 id="\u6027-\u6570-\u683C" tabindex="-1">\u6027, \u6570, \u683C <a class="header-anchor" href="#\u6027-\u6570-\u683C" aria-hidden="true">#</a></h2><p>\u4EBA\u79F0\u4EE3\u540D\u8A5E\u3060\u3051\u3067\u306F\u306A\u304F, \u540D\u8A5E, \u5F62\u5BB9\u8A5E, \u5B9A\u51A0\u8A5E\u306E\u3059\u3079\u3066\u306B\u304A\u3044\u3066\u6027, \u6570, \u683C\u306E\u533A\u5225\u304C\u3042\u308A\u307E\u3059. \u4E0D\u5B9A\u8A5E\u3068\u5F62\u5BB9\u8A5E\u306F\u4FEE\u98FE\u3057\u3066\u3044\u308B\u540D\u8A5E\u306B\u6027, \u6570, \u683C\u3092\u4E00\u81F4\u3055\u305B\u307E\u3059. \u305D\u306E\u305F\u3081\u8A9E\u9806\u306F\u304B\u306A\u308A\u81EA\u7531\u3067\u3059. \u540D\u8A5E\u306E\u6D3B\u7528\u306F\u7B2C\u4E00\u5909\u5316, \u7B2C\u4E8C\u5909\u5316, \u7B2C\u4E09\u5909\u5316\u306E\u4E09\u3064\u306E\u7A2E\u985E\u306B\u5927\u5225\u3055\u308C, \u305D\u308C\u305E\u308C\u7570\u306A\u308A\u307E\u3059. \u5F62\u5BB9\u8A5E\u306E\u6D3B\u7528\u306F\u7B2C\u4E00\u30FB\u7B2C\u4E8C\u5909\u5316\u3068\u7B2C\u4E09\u5909\u5316\u306E\u4E8C\u3064\u306B\u5927\u5225\u3055\u308C\u307E\u3059.</p><h3 id="\u6027" tabindex="-1">\u6027 <a class="header-anchor" href="#\u6027" aria-hidden="true">#</a></h3><p>\u540D\u8A5E\u306F\u5973\u6027, \u4E2D\u6027, \u7537\u6027\u306E\u4E09\u3064\u306E\u7A2E\u985E\u304C\u3042\u308A\u307E\u3059.</p><ul><li>\u5973\u6027\u540D\u8A5E : \u03BC\u03AE\u03C4\u03B7\u03C1\u300C\u6BCD\u300D \u03C6\u03C9\u03BD\u03AE\u300C\u58F0\u300D</li><li>\u4E2D\u6027\u540D\u8A5E : \u03C0\u03B1\u03B9\u03B4\u03AF\u03BF\u03BD\u300C\u5B50\u4F9B\u300D \u03B2\u03B9\u03B2\u03BB\u03AF\u03BF\u03BD\u300C\u672C\u300D</li><li>\u7537\u6027\u540D\u8A5E : \u03C0\u03B1\u03C4\u03AE\u03C1\u300C\u7236\u300D \u03BB\u03CC\u03B3\u03BF\u03C2\u300C\u8A00\u8449\u300D</li></ul><p>\u5F62\u5BB9\u8A5E\u3084\u5B9A\u51A0\u8A5E\u306E\u6027\u306F\u4FEE\u98FE\u3059\u308B\u540D\u8A5E\u306E\u6027\u306B\u547C\u5FDC\u3057\u307E\u3059. \u5F62\u5BB9\u8A5E\u306E\u6D3B\u7528\u306F\u7A2E\u985E\u306B\u3088\u308A\u307E\u3059\u304C,\u5F62\u5BB9\u8A5E\u3068\u540D\u8A5E\u3067\u5171\u901A\u3059\u308B\u6D3B\u7528\u3092\u6301\u3064\u5358\u8A9E\u3082\u3042\u308A\u307E\u3059. \u305D\u306E\u5834\u5408\u306F\u8A9E\u5C3E\u304C\u97FB\u3092\u8E0F\u3080\u5F62\u306B\u306A\u308A\u30D4\u30C3\u30C1\u30A2\u30AF\u30BB\u30F3\u30C8\u3082\u76F8\u307E\u3063\u3066\u72EC\u7279\u306E\u30EA\u30BA\u30E0\u3092\u751F\u307F\u51FA\u3057\u307E\u3059. \u58F0\u306B\u51FA\u3057\u3066\u8AAD\u3093\u3067\u307F\u308B\u3068\u697D\u3057\u3044\u304B\u3082\u3057\u308C\u307E\u305B\u3093. \u4E00\u3064\u76EE\u306E\u5358\u8A9E\u304C\u5B9A\u51A0\u8A5E\u3067, \u4E8C\u8A9E\u76EE\u304C\u300C\u826F\u3044, \u7F8E\u3057\u3044\u300D\u3092\u8868\u3059\u5F62\u5BB9\u8A5E\u3067\u3059.</p><ul><li>\u5973\u6027\u540D\u8A5E : <strong>\u1F21 \u03BA\u03B1\u03BB\u03AE \u03C6\u03C9\u03BD\u03AE</strong> \u305D\u306E\u7F8E\u3057\u3044\u58F0</li><li>\u4E2D\u6027\u540D\u8A5E : <strong>\u03C4\u03CC \u03BA\u03B1\u03BB\u03CC\u03BD \u03B2\u03B9\u03B2\u03BB\u03AF\u03BF\u03BD</strong> \u305D\u306E\u826F\u3044\u672C</li><li>\u7537\u6027\u540D\u8A5E : <strong>\u1F41 \u03BA\u03B1\u03BB\u03CC\u03C2 \u03BB\u03CC\u03B3\u03BF\u03C2</strong> \u305D\u306E\u7F8E\u3057\u3044\u8A00\u8449</li></ul><h3 id="\u6570" tabindex="-1">\u6570 <a class="header-anchor" href="#\u6570" aria-hidden="true">#</a></h3><p>\u5358\u6570/\u53CC\u6570/\u8907\u6570\u306E\u533A\u5225\u304C\u3042\u308A\u307E\u3057\u305F\u304C, \u53E4\u5178\u30AE\u30EA\u30B7\u30A2\u8A9E\u306E\u6642\u4EE3\u306B\u306F\u3059\u3067\u306B\u53CC\u6570\u306F\u8907\u6570\u3067\u4EE3\u7528\u3055\u308C\u308B\u3088\u3046\u306B\u306A\u308A\u307E\u3057\u305F. \u540D\u8A5E\u3092\u8907\u6570\u5F62\u306B\u3059\u308B\u3068\u304D\u306F\u305D\u306E\u540D\u8A5E\u306E\u8A9E\u5C3E\u3092\u6D3B\u7528\u3055\u305B\u307E\u3059. \u6D3B\u7528\u306E\u4ED5\u65B9\u306F\u540D\u8A5E\u306E\u7A2E\u985E\u306B\u3088\u308B\u306E\u3067\u4F8B\u3068\u3057\u3066\u3059\u3067\u306B\u51FA\u3066\u304D\u305F\u3082\u306E\u3092\u6D3B\u7528\u3055\u305B\u307E\u3059.</p><table><thead><tr><th style="text-align:left;">\u5358\u6570</th><th style="text-align:left;">\u8907\u6570</th></tr></thead><tbody><tr><td style="text-align:left;">\u03C6\u03C9\u03BD<strong>\u03AE</strong></td><td style="text-align:left;">\u03C6\u03C9\u03BD<strong>\u03B1\u03AF</strong></td></tr><tr><td style="text-align:left;">\u03B2\u03B9\u03B2\u03BB\u03AF<strong>\u03BF\u03BD</strong></td><td style="text-align:left;">\u03B2\u03B9\u03B2\u03BB\u03AF<strong>\u03B1</strong></td></tr><tr><td style="text-align:left;">\u03BB\u03CC\u03B3<strong>\u03BF\u03C2</strong></td><td style="text-align:left;">\u03BB\u03CC\u03B3<strong>\u03BF\u03B9</strong></td></tr></tbody></table><p>\u5F62\u5BB9\u8A5E\u306E\u6570\u306F\u6027\u306E\u6642\u3068\u540C\u3058\u3088\u3046\u306B\u540D\u8A5E\u306B\u547C\u5FDC\u3057\u307E\u3059.</p><table><thead><tr><th style="text-align:left;">\u5358\u6570</th><th style="text-align:left;">\u8907\u6570</th></tr></thead><tbody><tr><td style="text-align:left;">\u03BA\u03B1\u03BB<strong>\u03AE</strong> \u03C6\u03C9\u03BD<strong>\u03AE</strong></td><td style="text-align:left;">\u03BA\u03B1\u03BB<strong>\u03B1\u03AF</strong> \u03C6\u03C9\u03BD<strong>\u03B1\u03AF</strong></td></tr><tr><td style="text-align:left;">\u03BA\u03B1\u03BB<strong>\u03CC\u03BD</strong> \u03B2\u03B9\u03B2\u03BB\u03AF<strong>\u03BF\u03BD</strong></td><td style="text-align:left;">\u03BA\u03B1\u03BB<strong>\u03AC</strong> \u03B2\u03B9\u03B2\u03BB\u03AF<strong>\u03B1</strong></td></tr><tr><td style="text-align:left;">\u03BB\u03BA\u03B1\u03BB<strong>\u03CC\u03C2</strong> \u03BB\u03CC\u03B3<strong>\u03BF\u03C2</strong></td><td style="text-align:left;">\u03BA\u03B1\u03BB<strong>\u03BF\u03AF</strong> \u03BB\u03CC\u03B3<strong>\u03BF\u03B9</strong></td></tr></tbody></table><h3 id="\u683C" tabindex="-1">\u683C <a class="header-anchor" href="#\u683C" aria-hidden="true">#</a></h3><p>\u540D\u8A5E\u306B\u306F\u4E3B\u683C, \u5C5E\u683C, \u4E0E\u683C, \u5BFE\u683C, \u547C\u683C\u306E\u4E94\u3064\u306E\u683C\u304C\u3042\u308A\u307E\u3059. \u4E94\u3064\u306E\u683C\u306F\u8A9E\u5C3E\u306E\u6D3B\u7528\u306B\u3088\u3063\u3066\u8868\u3055\u308C\u307E\u3059. \u4EE5\u4E0B\u3067\u5177\u4F53\u4F8B\u3092\u793A\u3057\u307E\u3059.</p><ul><li>\u82F1\u8A9E : I hear the voice of a girl.</li><li>\u53E4\u5E0C\u8A9E : \u03BA\u03CC\u03C1\u03B7\u03C2 \u03C4\u1F74\u03BD \u03C6\u03C9\u03BD\u03AE\u03BD \u1F00\u03BA\u03BF\u03CD\u03C9.</li><li>\u9010\u8A9E\u8A33 : \u5C11\u5973\u306E (\u5B9A\u51A0\u8A5E) \u58F0\u3092 \u79C1\u304C\u805E\u304F</li><li>\u683C : \u683C \u5C5E\u683C \u5BFE\u683C \u52D5\u8A5E(\u4E00\u4EBA\u79F0\u5358\u6570)</li></ul><p>\u5F62\u5BB9\u8A5E\u306F\u6027\u3084\u6570\u3068\u540C\u69D8\u306B\u4FEE\u98FE\u3059\u308B\u540D\u8A5E\u306E\u683C\u306B\u547C\u5FDC\u3057\u3066\u6D3B\u7528\u3057\u307E\u3059.</p><ul><li>\u82F1\u8A9E : I hear the good voice of a clever girl.</li><li>\u53E4\u5E0C\u8A9E : \u03C3\u03BF\u03C6\u1FC6\u03C2 \u03BA\u03CC\u03C1\u03B7\u03C2 \u03C4\u1F74\u03BD \u03BA\u03B1\u03BB\u03AE\u03BD \u03C6\u03C9\u03BD\u03AE\u03BD \u1F00\u03BA\u03BF\u03CD\u03C9.</li><li>\u9010\u8A9E\u8A33 : \u8CE2\u3044 \u5C11\u5973\u306E (\u5B9A\u51A0\u8A5E) \u7F8E\u3057\u3044 \u58F0\u3092 \u79C1\u304C\u805E\u304F</li><li>\u683C : \u5C5E\u683C \u5BFE\u683C \u52D5\u8A5E (\u4E00\u4EBA\u79F0\u5358\u6570)</li></ul><p>\u524D\u7F6E\u8A5E\u306B\u3088\u3063\u3066\u306F\u5F8C\u308D\u306B\u304F\u308B\u540D\u8A5E\u306E\u683C\u304C\u6307\u5B9A\u3055\u308C, \u683C\u306B\u3088\u3063\u3066\u524D\u7F6E\u8A5E\u306E\u610F\u5473\u304C\u7570\u306A\u308B\u3053\u3068\u304C\u3042\u308A\u307E\u3059.</p><h2 id="\u52D5\u8A5E\u7D44\u7E54" tabindex="-1">\u52D5\u8A5E\u7D44\u7E54 <a class="header-anchor" href="#\u52D5\u8A5E\u7D44\u7E54" aria-hidden="true">#</a></h2><p>\u53E4\u5178\u30AE\u30EA\u30B7\u30A2\u8A9E\u306E\u52D5\u8A5E\u4F53\u7CFB\u306F\u5F62\u614B\u8AD6\u7684\u306B\u304B\u306A\u308A\u8907\u96D1\u3067, \u5370\u6B27\u7956\u8A9E\u306E\u59FF\u3092\u8272\u6FC3\u304F\u6B8B\u3057\u3066\u3044\u308B\u3068\u3055\u308C\u3066\u3044\u307E\u3059. \u4EE5\u4E0B\u306B\u793A\u3059\u76F8\u3084\u6642\u79F0, \u6CD5\u306E\u533A\u5225\u306F\u5168\u3066\u52D5\u8A5E\u306E\u8A9E\u5F62\u306B\u3088\u3063\u3066\u793A\u3055\u308C\u307E\u3059. \u8A9E\u5C3E\u306E\u9055\u3044\u304B\u3089\u52D5\u8A5E\u306B\u306F \u03C9 \u52D5\u8A5E\u3068 \u03BC\u03B9 \u52D5\u8A5E\u306E\u4E8C\u7A2E\u985E\u306B\u5206\u304B\u308C\u307E\u3059. \u7C21\u5358\u306E\u305F\u3081\u306B, \u4F8B\u306F\u5168\u3066 \u03C9 \u52D5\u8A5E\u306B\u3057\u3066\u3042\u308A\u307E\u3059.</p><h2 id="\u4EBA\u79F0" tabindex="-1">\u4EBA\u79F0 <a class="header-anchor" href="#\u4EBA\u79F0" aria-hidden="true">#</a></h2><p>\u4E3B\u8A9E\u306E\u4EBA\u79F0\u306B\u3088\u3063\u3066\u8A9E\u5C3E\u304C\u5909\u308F\u308A\u307E\u3059. \u4E00\u4EBA\u79F0, \u4E8C\u4EBA\u79F0, \u4E09\u4EBA\u79F0\u306E\u4E09\u7A2E\u985E\u306E\u533A\u5225\u304C\u3042\u308A\u307E\u3059. \u8A9E\u5F62\u304B\u3089\u4E3B\u8A9E\u304C\u63A8\u6E2C\u3067\u304D\u308B\u306E\u3067\u4E3B\u8A9E\u304C\u7701\u7565\u3055\u308C\u308B\u3053\u3068\u304C\u591A\u3044\u3067\u3059.</p><table><thead><tr><th style="text-align:left;">\u03C9\u52D5\u8A5E\u306E\u4F8B</th><th style="text-align:left;">\u65E5\u672C\u8A9E\u8A33</th></tr></thead><tbody><tr><td style="text-align:left;">\u1F00\u03BA\u03BF\u03CD<strong>\u03C9</strong></td><td style="text-align:left;">\u79C1\u304C\u805E\u304F</td></tr><tr><td style="text-align:left;">\u1F00\u03BA\u03BF\u03CD<strong>\u03B5\u03B9\u03C2</strong></td><td style="text-align:left;">\u3042\u306A\u305F\u304C\u805E\u304F</td></tr><tr><td style="text-align:left;">\u1F00\u03BA\u03BF\u03CD<strong>\u03B5\u03B9</strong></td><td style="text-align:left;">\u5F7C, \u5F7C\u5973\u304C\u805E\u304F</td></tr></tbody></table><h2 id="\u6570-1" tabindex="-1">\u6570 <a class="header-anchor" href="#\u6570-1" aria-hidden="true">#</a></h2><p>\u4E3B\u8A9E\u306E\u6570\u306B\u3088\u3063\u3066\u8A9E\u5C3E\u304C\u5909\u308F\u308A\u307E\u3059. \u5358\u6570, \u8907\u6570\u306E2\u7A2E\u985E\u306E\u533A\u5225\u304C\u3042\u308A\u307E\u3059. \u52D5\u8A5E\u306E\u8A9E\u5F62\u304B\u3089\u4E3B\u8A9E\u304C\u63A8\u6E2C\u3067\u304D\u308B\u306E\u3067\u4E3B\u8A9E\u304C\u7701\u7565\u3055\u308C\u308B\u3053\u3068\u304C\u591A\u3044\u3067\u3059.</p><table><thead><tr><th style="text-align:left;">\u03C9\u52D5\u8A5E\u306E\u4F8B</th><th style="text-align:left;">\u65E5\u672C\u8A9E\u8A33</th></tr></thead><tbody><tr><td style="text-align:left;">\u1F00\u03BA\u03BF\u03CD<strong>\u03BF\u03BC\u03B5\u03BD</strong></td><td style="text-align:left;">\u79C1\u305F\u3061\u304C\u805E\u304F</td></tr><tr><td style="text-align:left;">\u1F00\u03BA\u03BF\u03CD<strong>\u03B5\u03C4\u03B5</strong></td><td style="text-align:left;">\u3042\u306A\u305F\u305F\u3061\u304C\u805E\u304F</td></tr><tr><td style="text-align:left;">\u1F00\u03BA\u03BF\u03CD<strong>\u03BF\u03C5\u03C3\u03B9 (\u03BD)</strong></td><td style="text-align:left;">\u5F7C\u3089, \u5F7C\u5973\u3089\u304C\u805E\u304F</td></tr></tbody></table><h2 id="\u6CD5" tabindex="-1">\u6CD5 <a class="header-anchor" href="#\u6CD5" aria-hidden="true">#</a></h2><p>\u6CD5\u3068\u3057\u3066\u306F\u76F4\u8AAC\u6CD5, \u63A5\u7D9A\u6CD5, \u547D\u4EE4\u6CD5, \u5E0C\u6C42\u6CD5\u306E 4 \u3064\u304C\u3042\u308A\u307E\u3059. \u6CD5\u306F\u300C\u8A71\u8005\u304C\u52D5\u4F5C\u3084\u72B6\u614B\u3092\u3069\u306E\u7A0B\u5EA6\u4E8B\u5B9F\u3068\u3057\u3066\u78BA\u4FE1\u3057\u3066\u8FF0\u3079\u3066\u3044\u308B\u304B\u300D\u3092\u8868\u3057\u307E\u3059. \u3064\u307E\u308A4\u3064\u306E\u6CD5\u306F\u300C\u8A71\u8005\u306E\u65AD\u5B9A\u306E\u5EA6\u5408\u3044\u300D\u3092\u305D\u308C\u305E\u308C\u8868\u3057\u3066\u3044\u307E\u3059.</p><h3 id="\u76F4\u8AAC\u6CD5" tabindex="-1">\u76F4\u8AAC\u6CD5 <a class="header-anchor" href="#\u76F4\u8AAC\u6CD5" aria-hidden="true">#</a></h3><p>\u8A71\u8005\u304C\u305D\u306E\u52D5\u4F5C\u3092\u4E8B\u5B9F\u3068\u3057\u3066\u6271\u3044\u306A\u304C\u3089\u5358\u306B\u53D9\u8FF0\u3057\u3066\u3044\u308B\u3053\u3068\u3092\u8868\u3057\u307E\u3059. \u307E\u305F\u300C;\u300D\u3092\u8A9E\u5C3E\u306B\u3064\u3051\u308B\u3053\u3068\u3067\u76F4\u8AAC\u6CD5\u7591\u554F\u6587\u3092\u8868\u3057\u307E\u3059.</p><p>\u1F00\u03BA\u03BF\u03CD\u03B5\u03B9\u03C2;\u300C\u3042\u306A\u305F\u306F\u805E\u304F\u304B?\u300D</p><h3 id="\u63A5\u7D9A\u6CD5" tabindex="-1">\u63A5\u7D9A\u6CD5 <a class="header-anchor" href="#\u63A5\u7D9A\u6CD5" aria-hidden="true">#</a></h3><p>\u8A71\u8005\u304C\u305D\u306E\u52D5\u4F5C\u306E\u4E8B\u5B9F\u6027\u3092\u3042\u308B\u7A0B\u5EA6\u7591\u3044, \u307E\u305F\u306F\u8E8A\u8E87\u3057\u306A\u304C\u3089\u53D9\u8FF0\u3057\u3066\u3044\u308B\u3053\u3068\u3092\u8868\u3057\u307E\u3059. \u8A71\u8005\u306F\u305D\u306E\u52D5\u4F5C\u3084\u72B6\u614B\u30921\u3064\u306E\u53EF\u80FD\u6027\u3068\u3057\u3066\u6271\u3063\u3066\u3044\u307E\u3059.</p><p>\u1F00\u03BA\u03BF\u03CD\u03B7\u03B9\u03C2\u300C\u3042\u306A\u305F\u306F\u805E\u304F\u304B\u3082\u3057\u308C\u306A\u3044\u304C (\u3068\u3059\u308C\u3070 etc.)\u300D</p><h3 id="\u5E0C\u6C42\u6CD5" tabindex="-1">\u5E0C\u6C42\u6CD5 <a class="header-anchor" href="#\u5E0C\u6C42\u6CD5" aria-hidden="true">#</a></h3><p>\u8A71\u8005\u304C\u305D\u306E\u52D5\u4F5C\u306E\u4E8B\u5B9F\u6027\u3092\u304B\u306A\u308A\u7A0B\u5EA6\u7591\u3044, \u307E\u305F\u306F\u5927\u304D\u304F\u8E8A\u8E87\u3057\u306A\u304C\u3089\u53D9\u8FF0\u3057\u3066\u3044\u308B\u3053\u3068\u3092\u8868\u3057\u307E\u3059. \u63A5\u7D9A\u6CD5\u3068\u6BD4\u3079\u308B\u3068\u5F31\u3044\u53EF\u80FD\u6027\u3092\u793A\u3057, \u5C02\u3089\u300C\u5B9F\u73FE\u53EF\u80FD\u306A\u52D5\u4F5C\u306E\u9858\u671B\u300D\u3092\u8868\u73FE\u3059\u308B\u306E\u306B\u7528\u3044\u3089\u308C\u307E\u3059.</p><p>\u1F00\u03BA\u03BF\u03CD\u03BF\u03B9\u03C2\u300C\u3042\u306A\u305F\u304C\u805E\u3044\u3066\u304F\u308C\u308C\u3070\u826F\u3044\u304C (\u4EEE\u306B\u805E\u3044\u305F\u3068\u3057\u3066\u3082 etc.)\u300D</p><h3 id="\u547D\u4EE4\u6CD5" tabindex="-1">\u547D\u4EE4\u6CD5 <a class="header-anchor" href="#\u547D\u4EE4\u6CD5" aria-hidden="true">#</a></h3><p>\u8A71\u8005\u304C\u305D\u306E\u52D5\u4F5C\u3092\u81EA\u5206\u306E\u610F\u5FD7\u3092\u4ED6\u8005\u306B\u4F1D\u3048\u305F\u5F8C\u306B, \u4ED6\u8005\u304C\u610F\u5FD7\u3092\u50CD\u304B\u305B\u3066\u521D\u3081\u3066\u4E8B\u5B9F\u3068\u306A\u308B\u3088\u3046\u306A\u53EF\u80FD\u6027\u3068\u3057\u3066\u53D9\u8FF0\u3057\u3066\u3044\u308B\u3053\u3068\u3092\u8868\u3057\u307E\u3059. \u5E0C\u6C42\u6CD5\u3088\u308A\u3082\u3055\u3089\u306B\u5F31\u3044\u53EF\u80FD\u6027\u3092\u8868\u73FE\u3057\u307E\u3059.</p><p>\u1F00\u03BA\u03BF\u1FE6\u03B5\u300C\u805E\u3044\u3066\u304F\u3060\u3055\u3044 (\u805E\u3051 etc.)\u300D</p><h2 id="\u30A2\u30B9\u30DA\u30AF\u30C8-\u6642\u5236" tabindex="-1">\u30A2\u30B9\u30DA\u30AF\u30C8, \u6642\u5236 <a class="header-anchor" href="#\u30A2\u30B9\u30DA\u30AF\u30C8-\u6642\u5236" aria-hidden="true">#</a></h2><p>\u30AE\u30EA\u30B7\u30A2\u8A9E\u306E\u30A2\u30B9\u30DA\u30AF\u30C8\u3067\u306F, \u52D5\u4F5C\u305D\u306E\u3082\u306E\u304C\u3069\u3093\u306A\u73FE\u308C\u65B9 (\u69D8\u76F8) \u3067\u6349\u3048\u3089\u308C\u308B\u304B\u3092\u8868\u73FE\u3057\u307E\u3059. \u3053\u3053\u3067\u306E\u300C\u69D8\u76F8\u300D\u306F\u305D\u306E\u52D5\u4F5C\u304C\u5B9F\u969B\u306B\u3069\u3093\u306A\u8D77\u3053\u308A\u65B9\u3092\u3057\u305F\u304B\u3067\u306F\u306A\u304F, \u3042\u304F\u307E\u3067\u8A71\u8005\u304C\u305D\u308C\u3092\u3069\u306E\u3088\u3046\u306B\u6349\u3048\u3066\u3044\u308B\u304B\u304C\u57FA\u6E96\u306B\u306A\u308A\u307E\u3059. \u30A2\u30B9\u30DA\u30AF\u30C8\u306F\u4EE5\u4E0B\u306E3\u3064\u3067\u3059.</p><ol><li><p>\u70B9\u7684\u30A2\u30B9\u30DA\u30AF\u30C8 (\u30FB)<br> \u52D5\u4F5C\u306E\u751F\u8D77\u3092\u304B\u305F\u307E\u308A\u3068\u3057\u3066\u5168\u4F53\u3067\u6349\u3048\u308B\u30A2\u30B9\u30DA\u30AF\u30C8\u3067\u3059.</p></li><li><p>\u7DDA\u7684\u30A2\u30B9\u30DA\u30AF\u30C8 (\u2015)<br> \u52D5\u4F5C\u304C\u7D99\u7D9A\u3084\u9032\u884C, \u53CD\u5FA9\u3057\u3066\u3044\u308B\u3068\u8A71\u8005\u304C\u6349\u3048\u3066\u3044\u308B\u3068\u304D\u306E\u30A2\u30B9\u30DA\u30AF\u30C8\u3067\u3059.</p></li><li><p>\u70B9+\u7DDA\u7684\u30A2\u30B9\u30DA\u30AF\u30C8 (\u30FB\u2015)<br> \u52D5\u4F5C\u3092\u5B8C\u6210\u54C1\u3068\u3057\u3066\u6349\u3048, \u65E2\u6210\u4E8B\u5B9F\u3068\u3057\u3066\u773A\u3081\u308B\u3068\u304D\u306E\u30A2\u30B9\u30DA\u30AF\u30C8\u3067\u3059.</p></li></ol><p>\u76F4\u8AAC\u6CD5\u306B\u306F\u6642\u9593\u306B\u3088\u308B\u533A\u5225\u3068\u30A2\u30B9\u30DA\u30AF\u30C8\u306B\u3088\u308B\u533A\u5225\u3092\u639B\u3051\u5408\u308F\u305B\u308B\u3068 7 \u3064\u306E\u6642\u5236\u304C\u3042\u308A\u307E\u3059.</p><table><thead><tr><th style="text-align:center;"></th><th style="text-align:center;">\u70B9\u7684\u30A2\u30B9\u30DA\u30AF\u30C8</th><th style="text-align:center;">\u7DDA\u7684\u30A2\u30B9\u30DA\u30AF\u30C8</th><th style="text-align:center;">\u70B9+\u7DDA\u7684\u30A2\u30B9\u30DA\u30AF\u30C8</th></tr></thead><tbody><tr><td style="text-align:center;">\u672A\u6765</td><td style="text-align:center;">\u672A\u6765</td><td style="text-align:center;">\u672A\u6765</td><td style="text-align:center;">\u672A\u6765\u5B8C\u4E86</td></tr><tr><td style="text-align:center;">\u73FE\u5728</td><td style="text-align:center;">\u73FE\u5728</td><td style="text-align:center;">\u73FE\u5728</td><td style="text-align:center;">\u73FE\u5728\u5B8C\u4E86</td></tr><tr><td style="text-align:center;">\u904E\u53BB</td><td style="text-align:center;">\u30A2\u30AA\u30EA\u30B9\u30C8</td><td style="text-align:center;">\u672A\u5B8C\u4E86\u904E\u53BB</td><td style="text-align:center;">\u904E\u53BB\u5B8C\u4E86</td></tr></tbody></table><p>\u4F8B\u3048\u3070, \u03C0\u03B1\u03CD\u03C9\u300C\u6B62\u3081\u308B\u300D\u306F \u672A\u6765\u6642\u5236\u3067 \u03C0\u03B1\u03CD\u03C3\u03C9, \u30A2\u30AA\u30EA\u30B9\u30C8\u3067\u306F \u1F14\u03C0\u03B1\u03C5\u03C3\u03B1, \u904E\u53BB\u5B8C\u4E86\u3067\u306F \u1F10\u03C0\u03B5\u03C0\u03B1\u03CD\u03BA\u03B7, \u3068\u3044\u3063\u305F\u5177\u5408\u306B\u52A0\u97F3\u3084\u7573\u97F3\u3067\u305D\u308C\u305E\u308C\u306E\u69D8\u76F8\u3092\u8868\u3057\u307E\u3059.</p><p>\u4E00\u65B9\u63A5\u7D9A\u6CD5, \u547D\u4EE4\u6CD5\u3067\u306F 3 \u3064\u306E\u30A2\u30B9\u30DA\u30AF\u30C8\u306B\u3088\u308B\u533A\u5225\u306E\u307F\u3067\u3059.</p><table><thead><tr><th style="text-align:center;">\u70B9\u7684\u30A2\u30B9\u30DA\u30AF\u30C8</th><th style="text-align:center;">\u7DDA\u7684\u30A2\u30B9\u30DA\u30AF\u30C8</th><th style="text-align:center;">\u70B9+\u7DDA\u7684\u30A2\u30B9\u30DA\u30AF\u30C8</th></tr></thead><tbody><tr><td style="text-align:center;">\u30A2\u30AA\u30EA\u30B9\u30C8</td><td style="text-align:center;">\u672A\u5B8C\u4E86\u904E\u53BB</td><td style="text-align:center;">\u73FE\u5728\u5B8C\u4E86</td></tr></tbody></table><p>\u5E0C\u6C42\u6CD5\u3067\u306F\u63A5\u7D9A\u6CD5\u306E3\u3064\u306E\u30A2\u30B9\u30DA\u30AF\u30C8\u306B\u52A0\u3048\u3066\u6642\u9593\u3092\u8868\u3059\u305F\u3081\u306B\u672A\u6765\u5F62\u304C\u3042\u308A\u307E\u3059.</p><h2 id="\u614B" tabindex="-1">\u614B <a class="header-anchor" href="#\u614B" aria-hidden="true">#</a></h2><p>\u80FD\u52D5\u614B\u3068\u53D7\u52D5\u614B\u306B\u52A0\u3048\u3066\u4E2D\u52D5\u614B\u304C\u3042\u308A\u307E\u3059. \u80FD\u52D5\u614B\u304C\u81EA\u5206\u304B\u3089\u4ED6\u4EBA\u306B\u5411\u3051\u308B\u52D5\u4F5C, \u53D7\u52D5\u614B\u304C\u4ED6\u4EBA\u304B\u3089\u81EA\u5206\u306B\u5411\u3051\u3089\u308C\u305F\u52D5\u4F5C\u3092\u8868\u3057\u307E\u3059. \u3053\u306E\u8A00\u3044\u65B9\u3092\u7528\u3044\u308C\u3070\u4E2D\u52D5\u614B\u306F\u81EA\u5206\u304B\u3089\u81EA\u5206\u306B\u5411\u3051\u305F\u52D5\u4F5C\u3092\u8868\u3059\u3068\u8A00\u3048\u307E\u3057\u3087\u3046. \u5177\u4F53\u7684\u306B\u304A\u304A\u3088\u305D\u6B21\u306E3\u3064\u306E\u610F\u5473\u3092\u793A\u3057\u307E\u3059.</p><ul><li>\u300C\u81EA\u5206\u306B\u5BFE\u3057\u3066...\u3059\u308B\u300D</li><li>\u300C\u81EA\u5206\u306E\u305F\u3081\u306B...\u3059\u308B\u300D</li><li>\u300C\u81EA\u5206\u305F\u3061\u306E\u9593\u3067...\u3059\u308B\u300D</li></ul><p>\u305D\u3057\u3066\u4F8B\u306E\u5982\u304F\u52D5\u8A5E\u306E\u8A9E\u5F62\u306B\u3088\u3063\u3066\u793A\u3055\u308C\u307E\u3059. \u3057\u304B\u3057\u672A\u6765\u3068\u30A2\u30AA\u30EA\u30B9\u30C8\u4EE5\u5916\u306E\u6642\u5236\u3067\u306F\u4E2D\u52D5\u614B\u3068\u53D7\u52D5\u614B\u306E\u8A9E\u5F62\u306F\u540C\u3058\u3067\u3059. \u4E2D\u52D5\u614B\u306E\u8A9E\u5F62\u306B\u524D\u7F6E\u8A5E\u53E5&lt;\u1F51\u03C0\u03CC+\u5C5E\u683C&gt;\u300C...\u306B\u3088\u3063\u3066\u300D\u3092\u52A0\u3048\u308B\u3053\u3068\u3067\u53D7\u52D5\u614B\u3092\u8868\u3057\u307E\u3059.</p><ul><li>\u80FD\u52D5\u614B : \u03BB\u03BF\u03CD<strong>\u03C9</strong> \u03C4\u1F78\u03BD \u03B4\u03BF\u1FE6\u03BB\u03BF\u03BD. \u79C1\u304C\u53EC\u4F7F\u3044\u3092\u6D17\u3046</li><li>\u4E2D\u52D5\u614B : \u03BB\u03BF\u03CD<strong>\u03BF\u03BC\u03B1\u03B9</strong>. \u79C1\u304C\u79C1\u3092\u6D17\u3046</li><li>\u53D7\u52D5\u614B : \u03BB\u03BF\u03CD<strong>\u03BF\u03BC\u03B1\u03B9</strong> <strong>\u1F51\u03C0\u03CC</strong> <strong>\u03C4\u03BF\u1FE6</strong> \u03B4\u03BF\u03CD\u03BB\u03BF\u03C5. \u79C1\u306F\u53EC\u4F7F\u3044\u306B\u6D17\u308F\u308C\u308B</li></ul><h2 id="\u8A9E\u9806" tabindex="-1">\u8A9E\u9806 <a class="header-anchor" href="#\u8A9E\u9806" aria-hidden="true">#</a></h2><p>\u3053\u308C\u307E\u3067\u3067\u898B\u3066\u304D\u305F\u3088\u3046\u306B\u683C\u306E\u533A\u5225\u3092\u8A9E\u5F62\u306B\u3088\u3063\u3066\u793A\u305B\u308B\u306E\u3067\u8A9E\u9806\u306F\u304B\u306A\u308A\u81EA\u7531\u3067\u3059. \u8FF0\u8A9E\u52D5\u8A5E\u306F\u57FA\u672C\u7684\u306B\u6587\u672B\u306B\u6301\u3063\u3066\u304D\u307E\u3059\u304C\u5FC5\u305A\u3057\u3082\u305D\u3046\u3067\u306F\u3042\u308A\u307E\u305B\u3093. \u305D\u306E\u5834\u5408\u306F\u7279\u5225\u306A\u30CB\u30E5\u30A2\u30F3\u30B9\u3092\u5E2F\u3073\u307E\u305B\u3093.</p><p>\u3057\u304B\u3057\u5F62\u5BB9\u8A5E\u306F\u305D\u306E\u4F4D\u7F6E\u306B\u3088\u3063\u3066\u610F\u5473\u306E\u9055\u3044\u304C\u3042\u308A\u307E\u3059. \u5F62\u5BB9\u8A5E\u304C&lt;\u5B9A\u51A0\u8A5E+\u540D\u8A5E&gt;\u306E\u9593\u306B\u5272\u308A\u8FBC\u3080\u3068\u9650\u5B9A\u7528\u6CD5\u3068\u3057\u3066, \u305D\u308C\u4EE5\u5916\u3060\u3068\u53D9\u8FF0\u7528\u6CD5\u3068\u3057\u3066\u50CD\u304D\u307E\u3059.</p><ul><li>\u9650\u5B9A\u7528\u6CD5 : \u1F21 \u03BA\u03B1\u03BB\u03AE \u03C6\u03C9\u03BD\u03AE \u305D\u306E\u7F8E\u3057\u3044\u58F0</li><li>\u53D9\u8FF0\u7528\u6CD5 : \u03BA\u03B1\u03BB\u03AE \u1F21 \u03C6\u03C9\u03BD\u03AE \u305D\u306E\u58F0\u306F\u7F8E\u3057\u3044</li></ul><h2 id="\u6587\u5B57" tabindex="-1">\u6587\u5B57 <a class="header-anchor" href="#\u6587\u5B57" aria-hidden="true">#</a></h2><details class="details custom-block"><summary>\u6587\u5B57\u4E00\u89A7</summary><table><thead><tr><th style="text-align:center;">\u5927\u6587\u5B57</th><th style="text-align:center;">\u5C0F\u6587\u5B57</th><th style="text-align:center;">\u97F3\u4FA1</th><th style="text-align:center;">\u540D\u79F0</th></tr></thead><tbody><tr><td style="text-align:center;">\u0391</td><td style="text-align:center;">\u03B1</td><td style="text-align:center;">[a], [a\u02D0]</td><td style="text-align:center;">\u30A2\u30EB\u30D5\u30A1</td></tr><tr><td style="text-align:center;">\u0392</td><td style="text-align:center;">\u03B2</td><td style="text-align:center;">[b]</td><td style="text-align:center;">\u30D9\u30FC\u30BF</td></tr><tr><td style="text-align:center;">\u0393</td><td style="text-align:center;">\u03B3</td><td style="text-align:center;">[g], [\u014B]</td><td style="text-align:center;">\u30AC\u30F3\u30DE</td></tr><tr><td style="text-align:center;">\u0394</td><td style="text-align:center;">\u03B4</td><td style="text-align:center;">[d]</td><td style="text-align:center;">\u30C7\u30EB\u30BF</td></tr><tr><td style="text-align:center;">\u0395</td><td style="text-align:center;">\u03B5</td><td style="text-align:center;">[e]</td><td style="text-align:center;">\u30A8\u30FB\u30D7\u30FC\u30B7\u30ED\u30F3</td></tr><tr><td style="text-align:center;">\u0396</td><td style="text-align:center;">\u03B6</td><td style="text-align:center;">[zd]</td><td style="text-align:center;">\u30BC\u30FC\u30BF</td></tr><tr><td style="text-align:center;">\u0397</td><td style="text-align:center;">\u03B7</td><td style="text-align:center;">[\u025B\u02D0]</td><td style="text-align:center;">\u30A4\u30FC\u30BF</td></tr><tr><td style="text-align:center;">\u0398</td><td style="text-align:center;">\u03B8</td><td style="text-align:center;">[th]</td><td style="text-align:center;">\u30C6\u30FC\u30BF (\u30B7\u30FC\u30BF)</td></tr><tr><td style="text-align:center;">\u0399</td><td style="text-align:center;">\u03B9</td><td style="text-align:center;">[i], [i\u02D0]</td><td style="text-align:center;">\u30A4\u30AA\u30BF</td></tr><tr><td style="text-align:center;">\u039A</td><td style="text-align:center;">\u03BA</td><td style="text-align:center;">[k]</td><td style="text-align:center;">\u30AB\u30C3\u30D1</td></tr><tr><td style="text-align:center;">\u039B</td><td style="text-align:center;">\u03BB</td><td style="text-align:center;">[l]</td><td style="text-align:center;">\u30E9\u30E0\u30C0</td></tr><tr><td style="text-align:center;">\u039C</td><td style="text-align:center;">\u03BC</td><td style="text-align:center;">[m]</td><td style="text-align:center;">\u30DF\u30E5\u30FC</td></tr><tr><td style="text-align:center;">\u039D</td><td style="text-align:center;">\u03BD</td><td style="text-align:center;">[n]</td><td style="text-align:center;">\u30CB\u30E5\u30FC</td></tr><tr><td style="text-align:center;">\u039E</td><td style="text-align:center;">\u03BE</td><td style="text-align:center;">[ks]</td><td style="text-align:center;">\u30AF\u30B7\u30FC</td></tr><tr><td style="text-align:center;">\u039F</td><td style="text-align:center;">\u03BF</td><td style="text-align:center;">[o]</td><td style="text-align:center;">\u30AA\u30DF\u30AF\u30ED\u30F3</td></tr><tr><td style="text-align:center;">\u03A0</td><td style="text-align:center;">\u03C0</td><td style="text-align:center;">[p]</td><td style="text-align:center;">\u30D4\u30FC (\u30D1\u30A4)</td></tr><tr><td style="text-align:center;">\u03A1</td><td style="text-align:center;">\u03C1</td><td style="text-align:center;">[r]</td><td style="text-align:center;">\u30ED\u30FC</td></tr><tr><td style="text-align:center;">\u03A3</td><td style="text-align:center;">\u03C3/\u03C2</td><td style="text-align:center;">[s], [z]</td><td style="text-align:center;">\u30B7\u30FC\u30B0\u30DE</td></tr><tr><td style="text-align:center;">\u03A4</td><td style="text-align:center;">\u03C4</td><td style="text-align:center;">[t]</td><td style="text-align:center;">\u30BF\u30A6</td></tr><tr><td style="text-align:center;">\u03A5</td><td style="text-align:center;">\u03C5</td><td style="text-align:center;">[y], [y\u02D0]</td><td style="text-align:center;">\u30E6\u30FC\u30FB\u30D7\u30FC\u30B7\u30ED\u30F3</td></tr><tr><td style="text-align:center;">\u03A6</td><td style="text-align:center;">\u03C6</td><td style="text-align:center;">[ph]</td><td style="text-align:center;">\u30D4\u30FC (\u30D5\u30A1\u30A4)</td></tr><tr><td style="text-align:center;">\u03A7</td><td style="text-align:center;">\u03C7</td><td style="text-align:center;">[kh]</td><td style="text-align:center;">\u30AD\u30FC (\u30AB\u30A4)</td></tr><tr><td style="text-align:center;">\u03A8</td><td style="text-align:center;">\u03C8</td><td style="text-align:center;">[ps]</td><td style="text-align:center;">\u30D7\u30B7\u30FC</td></tr><tr><td style="text-align:center;">\u03A9</td><td style="text-align:center;">\u03C9</td><td style="text-align:center;">[\u0254\u02D0]</td><td style="text-align:center;">\u30AA\u30FC\u30FB\u30E1\u30AC</td></tr></tbody></table></details><h2 id="\u53C2\u8003\u6587\u732E" tabindex="-1">\u53C2\u8003\u6587\u732E <a class="header-anchor" href="#\u53C2\u8003\u6587\u732E" aria-hidden="true">#</a></h2><ul><li>\u5800\u5DDD\u5B8F,\u300E\u3057\u3063\u304B\u308A\u5B66\u3076\u521D\u7D1A\u53E4\u5178\u30AE\u30EA\u30B7\u30E3\u8A9E\u300F, \u30D9\u30EC\u51FA\u7248, 2021</li><li>\u7E54\u7530\u662D,\u300E\u65B0\u7D04\u8056\u66F8\u306E\u30AE\u30EA\u30B7\u30A2\u8A9E\u6587\u6CD5I\u300F, \u6559\u53CB\u793E, 2003</li></ul>',62);function y(e,h,o,p,f,u){return i(),n("div",null,[t("h1",g,[d(r(e.$frontmatter.title)+" ",1),c]),x])}const v=l(s,[["render",y]]);export{_ as __pageData,v as default};
