import{_ as a,c as l,b as t,d as r,t as d,a as n,o as i}from"./app.f3774844.js";const b=JSON.parse('{"title":"\u97D3\u56FD\u671D\u9BAE\u8A9E / \uD55C\uAD6D\uC5B4","description":"","frontmatter":{"title":"\u97D3\u56FD\u671D\u9BAE\u8A9E / \uD55C\uAD6D\uC5B4","section":"\u8A00\u8A9E\u3054\u3068\u306E\u6982\u8AAC"},"headers":[{"level":2,"title":"\u7CFB\u7D71","slug":"\u7CFB\u7D71"},{"level":2,"title":"\u6027, \u6570, \u683C","slug":"\u6027-\u6570-\u683C"},{"level":2,"title":"\u6642\u5236, \u30A2\u30B9\u30DA\u30AF\u30C8","slug":"\u6642\u5236-\u30A2\u30B9\u30DA\u30AF\u30C8"},{"level":2,"title":"\u97F3\u97FB","slug":"\u97F3\u97FB"},{"level":2,"title":"\u30AA\u30CE\u30DE\u30C8\u30DA","slug":"\u30AA\u30CE\u30DE\u30C8\u30DA"},{"level":2,"title":"\u6587\u5B57, \u767A\u97F3","slug":"\u6587\u5B57-\u767A\u97F3"},{"level":2,"title":"\u53C2\u8003\u6587\u732E","slug":"\u53C2\u8003\u6587\u732E"}],"relativePath":"docs/kor/index.md","lastUpdated":1661240602000}'),s={name:"docs/kor/index.md"},h={id:"frontmatter-title",tabindex:"-1"},c=t("a",{class:"header-anchor",href:"#frontmatter-title","aria-hidden":"true"},"#",-1),o=n('<h2 id="\u7CFB\u7D71" tabindex="-1">\u7CFB\u7D71 <a class="header-anchor" href="#\u7CFB\u7D71" aria-hidden="true">#</a></h2><p>\u97D3\u56FD\u671D\u9BAE\u8A9E\u306F\u30A2\u30EB\u30BF\u30A4\u8A9E\u65CF\u3060\u3068\u3044\u3046\u8AAC\u304C\u5B9A\u8AAC\u3067\u3057\u305F\u304C, \u73FE\u4EE3\u306B\u304A\u3044\u3066\u306F\u4E00\u3064\u306E\u8A9E\u65CF (\u5B64\u7ACB\u8A9E)\u3092\u306A\u3057\u3066\u3044\u3066, \u97D3\u56FD\u671D\u9BAE\u8A9E\u65CF\u3068\u3044\u3046\u4E00\u3064\u306E\u8A9E\u7FA4\u304C\u3042\u308B\u3068\u3044\u3046\u4EEE\u8AAC\u3082\u5F37\u304F\u652F\u6301\u3055\u308C\u3066\u3044\u307E\u3059\u304C, \u97D3\u56FD\u671D\u9BAE\u8A9E\u306E\u7531\u6765\u306F\u672A\u3060\u306F\u3063\u304D\u308A\u3057\u3066\u3044\u307E\u305B\u3093.</p><h2 id="\u6027-\u6570-\u683C" tabindex="-1">\u6027, \u6570, \u683C <a class="header-anchor" href="#\u6027-\u6570-\u683C" aria-hidden="true">#</a></h2><p>\u97D3\u56FD\u671D\u9BAE\u8A9E\u306B\u306F\u6027\u3084\u6570\u306E\u6982\u5FF5\u306F\u3042\u308A\u307E\u305B\u3093. \u97D3\u56FD\u671D\u9BAE\u8A9E\u306F\u81A0\u7740\u8A9E\u3067\u3042\u308A, \u65E5\u672C\u8A9E\u3068\u540C\u69D8\u306B\u683C\u3092\u52A9\u8A5E\u306B\u3088\u308A\u6A19\u8B58\u3057\u307E\u3059.</p><h2 id="\u6642\u5236-\u30A2\u30B9\u30DA\u30AF\u30C8" tabindex="-1">\u6642\u5236, \u30A2\u30B9\u30DA\u30AF\u30C8 <a class="header-anchor" href="#\u6642\u5236-\u30A2\u30B9\u30DA\u30AF\u30C8" aria-hidden="true">#</a></h2><p>\u300C\u79C1\u306F\u97D3\u56FD\u306B\u6765\u308B\u98DB\u884C\u6A5F\u306E\u4E2D\u3067\u5F7C\u306B\u4F1A\u3044\u307E\u3057\u305F\u300D\u306E\u3088\u3046\u306B, \u300C\u6765\u308B\u300D\u3068\u3044\u3046\u52D5\u8A5E\u306F\u4F1A\u3046\u3068\u3044\u3046\u884C\u70BA\u304C\u884C\u308F\u308C\u305F\u6642\u70B9\u3067\u306E\u884C\u70BA\u3067\u3059\u304C, \u73FE\u5728\u5F62\u3092\u4F7F\u3063\u3066\u8868\u3057\u307E\u3059. \u97D3\u56FD\u671D\u9BAE\u8A9E\u306E\u6642\u5236\u306F\u767A\u8A71\u3057\u305F\u6642\u3092\u57FA\u6E96\u306B\u3059\u308B\u3053\u3068\u3082, \u6587\u7AE0\u306E\u4E2D\u306E\u52D5\u8A5E\u3092\u57FA\u6E96\u306B\u76F8\u5BFE\u7684\u306A\u6642\u70B9\u3092\u793A\u3059\u3053\u3068\u3082\u3067\u304D\u307E\u3059. \u97D3\u56FD\u671D\u9BAE\u8A9E\u306E\u6A19\u6E96\u6587\u6CD5\u306B\u95A2\u3057\u3066\u7814\u7A76\u3059\u308B\u6A5F\u95A2\u3067\u3042\u308B\u97D3\u56FD\u306E\u56FD\u7ACB\u56FD\u8A9E\u9662\u306B\u3088\u308B\u3068, \u97D3\u56FD\u671D\u9BAE\u8A9E\u306E\u6642\u5236\u306F\u672A\u3060\u306F\u3063\u304D\u308A\u3057\u3066\u304A\u3089\u305A, \u6642\u5236\u304C\u306A\u3044\u3068\u3044\u3046\u610F\u898B\u3084\u6587\u6CD5\u7684\u306B\u904E\u53BB\u304B\u975E\u904E\u53BB\u3057\u304B\u5B58\u5728\u3057\u306A\u3044\u3068\u3044\u3046\u610F\u898B\u306A\u3069, \u69D8\u3005\u306A\u898B\u89E3\u304C\u76DB\u3093\u306B\u8B70\u8AD6\u3055\u308C\u3066\u3044\u308B\u305D\u3046\u3067\u3059.</p><p>\u97D3\u56FD\u671D\u9BAE\u8A9E\u306B\u306F\u672A\u6765\u3092\u8868\u3059\u7D42\u7D50\u8A9E\u5C3E\u304C\u5B58\u5728\u3057\u306A\u3044\u305F\u3081, \u610F\u5FD7\u3092\u8868\u3059\u9023\u4F53\u5F62\u3092\u4F7F\u3063\u3066\u672A\u6765\u3092\u8868\u3059\u306E\u3082\u97D3\u56FD\u671D\u9BAE\u8A9E\u306E\u7279\u5FB4\u306E\u4E00\u3064\u3067\u3059.</p><ul><li>\u97D3\u56FD\u671D\u9BAE\u8A9E : \uB098\uB294 \uBC25\uC744 \uBA39\uC744 \uAC83 \uC774\uB2E4.</li><li>\u65E5\u8A9E\u9010\u8A9E\u8A33 : \u79C1\u306F \u3054\u98EF\u3092 \u98DF\u3079\u308B (\u610F\u5FD7\u306E\u8A9E\u5C3E-\uC744) \u3053\u3068 (\u5F62\u5F0F\u540D\u8A5E) \u3060</li></ul><h2 id="\u97F3\u97FB" tabindex="-1">\u97F3\u97FB <a class="header-anchor" href="#\u97F3\u97FB" aria-hidden="true">#</a></h2><p>\u97D3\u56FD\u671D\u9BAE\u8A9E\u3067\u306F\u4F3C\u3066\u3044\u308B\u6BCD\u97F3\u7FA4\u540C\u58EB\u306B\u7D50\u5408\u3057\u3088\u3046\u3068\u3059\u308B\u6BCD\u97F3\u8ABF\u548C\u304C\u5F37\u304F\u73FE\u308C\u307E\u3059.</p><table><thead><tr><th style="text-align:center;">\u7A2E\u985E</th><th style="text-align:left;">\u6587\u5B57</th></tr></thead><tbody><tr><td style="text-align:center;">\u967D\u6027\u6BCD\u97F3</td><td style="text-align:left;">\u1161, \u1162, \u1163, \u1164, \u1169, \u116A, \u116B, \u116C, \u116D</td></tr><tr><td style="text-align:center;">\u9670\u6027\u6BCD\u97F3</td><td style="text-align:left;">\u1165, \u1166, \u1167, \u1168, \u116E, \u116F, \u1170, \u1171, \u1172, \u1173, \u1174</td></tr><tr><td style="text-align:center;">\u4E2D\u6027\u6BCD\u97F3</td><td style="text-align:left;">\u1175</td></tr></tbody></table><p>\u6BCD\u97F3\u8ABF\u548C\u306F\u30CF\u30F3\u30B0\u30EB\u306E\u5F62\u3068\u3082\u5F37\u304F\u95A2\u9023\u3057\u3066\u3044\u307E\u3059\u304C, \u660E\u308B\u3044\u611F\u3058\u306E\u967D\u6027\u6BCD\u97F3\u306E\u6BCD\u97F3\u306F\u592A\u967D\u304C\u4EBA\u306E\u53F3\u306B\u3042\u308B\u6A21\u69D8, \u592A\u967D\u304C\u6771\u306B\u3042\u308B\u6A21\u69D8\u3092\u8868\u73FE\u3057\u305F\u3082\u306E\u3067\u3059. \u9670\u6027\u6BCD\u97F3\u306F\u591A\u5C11\u6697\u3044\u611F\u3058\u3067, \u592A\u967D\u304C\u6C88\u3093\u3060\u6A21\u69D8\u3068, \u592A\u967D\u304C\u897F\u306B\u3042\u308B\u6A21\u69D8\u7B49\u3092\u8868\u73FE\u3057\u305F\u3082\u306E\u3067\u3059.</p><table><thead><tr><th style="text-align:center;">\u52D5\u8A5E</th><th style="text-align:left;">\u57FA\u672C\u5F62\u30FB\u767A\u97F3</th><th style="text-align:center;">\u5206\u985E</th><th style="text-align:left;">\u904E\u53BB\u5F62\u30FB\u767A\u97F3</th></tr></thead><tbody><tr><td style="text-align:center;">\u66F8\u304F</td><td style="text-align:left;">\uC4F0\uB2E4 [sseuda]</td><td style="text-align:center;">\u9670\u6027 (\u1173\u306F\u9670\u6027\u6BCD\u97F3)</td><td style="text-align:left;">\uC37C\uB2E4 [sseot-dda]</td></tr><tr><td style="text-align:center;">\u884C\u304F</td><td style="text-align:left;">\uAC00\uB2E4 [gada]</td><td style="text-align:center;">\u967D\u6027 (\u1161\u306F\u967D\u6027\u6BCD\u97F3)</td><td style="text-align:left;">\uAC14\uB2E4 [gat-dda]</td></tr></tbody></table><p>\u6D3B\u7528\u3092\u3057\u305F\u5F8C\u3082\u6BCD\u97F3\u7FA4\u306E\u7279\u6027\u3092\u4FDD\u3061 (\u9670\u6027\u6BCD\u97F3\u1173\u2192\u9670\u6027\u6BCD\u97F3\u1165, \u967D\u6027\u6BCD\u97F3\u1161\u2192\u1161), \u305D\u306E\u7279\u6027\u304C\u5909\u308F\u3089\u306A\u3044\u3053\u3068\u304C\u5206\u304B\u308A\u307E\u3059.</p><h2 id="\u30AA\u30CE\u30DE\u30C8\u30DA" tabindex="-1">\u30AA\u30CE\u30DE\u30C8\u30DA <a class="header-anchor" href="#\u30AA\u30CE\u30DE\u30C8\u30DA" aria-hidden="true">#</a></h2><p>\u97D3\u56FD\u671D\u9BAE\u8A9E\u3067\u306F\u69D8\u3005\u306A\u30AA\u30CE\u30DE\u30C8\u30DA\u304C\u3042\u308A, \u5F62\u3092\u8868\u3059\u64EC\u614B\u8A9E, \u97F3\u3092\u8868\u3059\u64EC\u58F0\u8A9E\u306B\u5206\u304B\u308C\u3066\u65E5\u5E38\u751F\u6D3B\u3067\u3082\u5E83\u304F\u4F7F\u308F\u308C\u3066\u3044\u307E\u3059. \u30AA\u30CE\u30DE\u30C8\u30DA\u3092\u4E0A\u624B\u304F\u4F7F\u3046\u3068\u8868\u73FE\u304C\u3068\u3066\u3082\u8C4A\u304B\u306B\u306A\u308B\u306E\u3067, \u97D3\u56FD\u671D\u9BAE\u8A9E\u3092\u5B66\u3073\u305F\u3044\u3068\u601D\u3046\u65B9\u306F\u305C\u3072\u8EAB\u306B\u4ED8\u3051\u3066\u304F\u3060\u3055\u3044.</p><table><thead><tr><th style="text-align:center;">\u64EC\u614B\u8A9E</th><th style="text-align:center;"></th><th style="text-align:center;">\u64EC\u58F0\u8A9E</th><th style="text-align:center;"></th></tr></thead><tbody><tr><td style="text-align:center;">\uAE61\uCD1D\uAE61\uCD1D<br>[kkang-chong-kkang-chong]</td><td style="text-align:center;">\u30A6\u30B5\u30AE\u304C\u8EFD\u304F\u98DB\u3076\u69D8<br>(\u967D\u6027\u6BCD\u97F3)</td><td style="text-align:center;">\uCF5C\uB85D\uCF5C\uB85D<br>[col-loc-col-loc]</td><td style="text-align:center;">\u30B3\u30F3\u30B3\u30F3<br>(\u967D\u6027\u6BCD\u97F3)</td></tr><tr><td style="text-align:center;">\uAED1\uCDA9\uAED1\uCDA9<br>[kkeong-chung-kkeong-chung]</td><td style="text-align:center;">\u30A6\u30B5\u30AE\u304C\u5927\u304D\u304F\u98DB\u3076\u69D8<br>(\u9670\u6027\u6BCD\u97F3)</td><td style="text-align:center;">\uCFE8\uB7ED\uCFE8\uB7ED<br>[cul-luck-cul-luck]</td><td style="text-align:center;">\u30B4\u30DC\u30B4\u30DC<br>(\u9670\u6027\u6BCD\u97F3)</td></tr></tbody></table><p>\u540C\u3058\u30AA\u30CE\u30DE\u30C8\u30DA\u3067\u3082, \u6BCD\u97F3\u7FA4\u3092\u5207\u308A\u66FF\u3048\u308B\u3053\u3068\u3067\u69D8\u3005\u306A\u8868\u73FE\u304C\u3067\u304D\u307E\u3059.</p><h2 id="\u6587\u5B57-\u767A\u97F3" tabindex="-1">\u6587\u5B57, \u767A\u97F3 <a class="header-anchor" href="#\u6587\u5B57-\u767A\u97F3" aria-hidden="true">#</a></h2><p>\u97D3\u56FD\u671D\u9BAE\u8A9E\u306F, \u671D\u9BAE\u6642\u4EE3\u306E\u4E16\u5B97\u5927\u738B\u304C\u4F5C\u3063\u305F\u30CF\u30F3\u30B0\u30EB\u3068\u3044\u3046\u6587\u5B57\u3092\u63A1\u629E\u3057\u3066\u8868\u8A18\u3057\u3066\u3044\u307E\u3059. \u30CF\u30F3\u30B0\u30EB\u306F\u8868\u97F3\u6587\u5B57\u3067\u3042\u308A, \u6BCD\u97F3\u3092\u69CB\u6210\u3059\u308B\u30D1\u30FC\u30C4\u3068\u5B50\u97F3\u3092\u69CB\u6210\u3059\u308B\u30D1\u30FC\u30C4\u3092\u7D44\u307F\u5408\u308F\u305B\u3066\u6587\u5B57\u3092\u4F5C\u308A\u307E\u3059. \u30CF\u30F3\u30B0\u30EB\u306E\u9020\u5B57\u539F\u7406\u306B\u3064\u3044\u3066\u8FF0\u3079\u3066\u3044\u308B\u8A13\u6C11\u6B63\u97F3\u306B\u3088\u308B\u3068, \u6BCD\u97F3\u306F\u5929\u3068\u5730\u3068\u4EBA\u306E\u95A2\u4FC2, \u5B50\u97F3\u306F\u53E3\u3068\u820C\u306E\u5F62\u3092\u8003\u3048\u3066\u8003\u6848\u3057\u305F\u305D\u3046\u3067\u3059.</p><p>\u6BCD\u97F3\u306E\u57FA\u672C\u3068\u306A\u308B\u97F3\u306F\u30FB (\u5929), \u1173 (\u5730), \u1175 (\u4EBA) \u306E\u4E09\u3064\u304C\u3042\u3063\u3066, \u30FB\u306F\u53E3\u3092\u5E83\u304F\u3057\u305F\u308A\u72ED\u304F\u3057\u305F\u308A\u3057\u3066\u51FA\u3059\u97F3, \u1175\u306F\u30A4\u306E\u97F3, \u1173\u306F\u30A6\u3092\u767A\u97F3\u3057\u306A\u304C\u3089\u53E3\u89D2\u3092\u4E0A\u3052\u308B\u97F3\u3067\u3059. \u5B50\u97F3\u306F\u820C\u306E\u6839\u672C\u3092\u53E3\u306E\u5929\u4E95\u306B\u8FD1\u3065\u3051\u3066\u51FA\u3059\u97F3\u306E (\u1100 [g]), \u820C\u306E\u5148\u3092\u6B6F\u306B\u6469\u64E6\u3055\u305B\u3066\u51FA\u3059\u97F3\u306E(\u1102 [n]), \u53E3\u3092\u5E83\u304F\u958B\u3051\u3066\u51FA\u3059\u97F3\u306E (\u1106 [m]), \u820C\u306E\u5148\u3092\u6B6F\u306B\u8FD1\u3065\u3051\u3066\u7A7A\u6C17\u3092\u5410\u304D\u306A\u304C\u3089\u51FA\u3059\u97F3\u306E (\u1109 [s]) \u306A\u3069\u304C\u3042\u308A\u307E\u3059.</p><h2 id="\u53C2\u8003\u6587\u732E" tabindex="-1">\u53C2\u8003\u6587\u732E <a class="header-anchor" href="#\u53C2\u8003\u6587\u732E" aria-hidden="true">#</a></h2><ul><li><a href="https://www.korean.go.kr/front/onlineQna/onlineQnaView.do?mn_id=216&amp;qna_seq=123599" target="_blank" rel="noreferrer">\uAD6D\uB9BD\uAD6D\uC5B4\uC6D0 \uC628\uB77C\uC778\uAC00\uB098\uB2E4</a>, \u6700\u7D42\u95B2\u89A7\u65E5 2022/5/29</li><li>\uBC15\uC9C4\uD638,\u201D\uC2DC\uC81C, \uC0C1, \uC591\uD0DC\u201D, 2011</li><li>\uAD6D\uB9BD\uD55C\uAE00\uBC15\uBB3C\uAD00,\u201D\uC27D\uAC8C \uC77D\uB294 \uD6C8\uBBFC\uC815\uC74C\u201D, 2020</li></ul>',23);function g(e,x,y,p,f,u){return i(),l("div",null,[t("h1",h,[r(d(e.$frontmatter.title)+" ",1),c]),o])}const k=a(s,[["render",g]]);export{b as __pageData,k as default};
